/************************
*  공통 Script Include  *
************************/
#include "lib::can_common_script.js";
#include "lib::can_app_script.js";


/*********************
*  화면 변수 선언부  *
*********************/
var sSearchKind  = "";                                 //본 화면에선 사용하지않음
var sInputValue  = "";                                 //기본 검색 조건
var sCommonGubn  = "";                                 //본 화면에선 사용하지않음 

/*-----------------------------------+
|  최초 화면 Load時 처리 할 사항     |
+------------------------------------*/
function fcFormLoadSetting(obj){

	edAddr.Text = sInputValue;       //Text Box Display

}

/*-----------------------+
|  우편번호 조회 실행 !  |
+------------------------*/
function fcSearch(obj) {	
	//필수 조회 조건 체크
	if ( !fnc_IsMandatory(edAddr, "주소(동)") ) {
		return;
	}
	
	var sServiceName = "post-service:search";                 // 서버에 요청할 Sevice 명
	var sInDataSet   = "";                                  // Input DataSet 명칭
	var sOutDataSet  = "dsOutPost=ds_post";                 // Output DataSet 명칭

    fcd_AddParam("DONG", trim(edAddr.value));
	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fn_PostCallBack", false);
	
	grdMaster.SetFocus();
}
/*------------------------------------+
|  Transaction 후 처리 해야 할 내용!  |
+-------------------------------------*/
function fn_PostCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
}

/*--------------+
|  확인 처리 !  |
+---------------*/
function fcChoice(obj) {

	var arrReturn    = array(2);               //결과 반환 할 Array
	
	arrReturn[0] = dsOutPost.GetColumn(dsOutPost.currow, "ZIPCODE");   //우편번호 반환
	arrReturn[1] = dsOutPost.GetColumn(dsOutPost.currow, "ADDR");      //주소 반환
    Close(arrReturn);                                                  //반환 후 조회
}

/*--------------+
|  취소 처리 !  |
+---------------*/
function fcClose(obj) {
	Close();     //Null 반환 후 종료
}

/*---------------------------------+
|  Enter Key Press 시 조회 실행 !  |
+----------------------------------*/
function edSearch_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam){

	if (nChar==13) fcSearch();

}

/*-----------------------------------------------+
|  그리드에서 더블 클릭 시 선택과 동일한 작업 !  |
+------------------------------------------------*/
function grdMaster_OnCellDblClick(obj,nRow,nCell,nX,nY) {

	// 배열에 해당 값을 반환 하고 종료
	fcChoice();

}

/*-----------------------------------------------+
|  그리드에서 Enter Key 시 선택과 동일한 작업 !  |
+------------------------------------------------*/
function grdMaster_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam) {
	
	// 엔터키 입력 시 배열에 해당 값을 반환 하고 종료
	if (nChar==13) fcChoice();

}
