function fcSearch(obj) {
	
	var sServiceName = "d01000003-service:searchGrpMenu";
    var sInDataSet   = "";
    var sOutDataSet  = "dsMenuGrpValue=dsMenuGrpValue";
    
    fcd_AddParam("MN_ID", cbMenuGrp.Value);
    
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);     
}
/*-------------------------------------------------------------------------------------------------+
>>  메뉴리스트 조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchMenuList(obj,nOldRow,nRow) {
	if ( dsMenuGrpValue.GetRowType(dsMenuGrpValue.currow) == "insert" ) {
        edGrpMnID.Readonly = false;
	} else {
        edGrpMnID.Readonly = true;
	}
	
	fnc_SetBKColor(this);

    var sServiceName = "d01000003-service:searchMenu";
    var sInDataSet   = "";
    var sOutDataSet  = "dsMenuListValue=dsMenuListValue";
    
    fcd_AddParam("UP_MN_ID", dsMenuGrpValue.GetColumn(dsMenuGrpValue.currow, "MN_ID"));
     
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);       
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크	
	if ( !(fnc_DatasetChangeCheck(dsMenuGrpValue) || fnc_DatasetChangeCheck(dsMenuListValue)))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}
	
	// 필수항목체크
	oDataSet          = dsMenuGrpValue;
	sMandatoryColID   = "MN_ID|MN_NM";
	sMandatoryColName = "메뉴ID|메뉴명";
	oGrid             = grdMenuGrp;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	
	// 필수항목체크
	oDataSet          = dsMenuListValue;
	sMandatoryColID   = "MN_ID|SCRN_ID|MN_NM|UP_MN_ID|URL";
	sMandatoryColName = "메뉴ID|화면ID|메뉴명|상위메뉴ID|URL";
	oGrid             = grdMenuList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	if (SubStr(edUrl.Text,0,6) != "form::"){
		fnc_Message("URL은 form:: 로 시작해야 합니다.", "N", "");
		edUrl.SetFocus();
		 return false;
	 }
    if (Length(edMenuId.Text) != 9){
		fnc_Message("메뉴id는 9자리이어야 합니다.", "N", "");
		edMenuId.SetFocus();
		return false;
    }
    return true;
}

function fcSaveSet() {

   for(var j=0; j<dsMenuListValue.rowcount; j++) {
	   if(trim(SubStr(dsMenuListValue.GetColumn(j, "UP_MN_ID"),0,5)) != trim(SubStr(dsMenuListValue.GetColumn(j, "MN_ID"),0,5))){
			fnc_Message("상위메뉴 5자리와 일치하지 않습니다 !", "N", "");
			edMenuId.SetFocus();
			return false;
	   }
	   
   }
   return true;
}

function fcSave(obj) {
   
    if ( !fcSaveCheck(obj) ) return;
    if ( !fcSaveSet() ) return;

    var sServiceName = "d01000003-service:saveMenu";
    var sInDataSet   = "dsMenuGrpValue=dsMenuGrpValue:u ";
        sInDataSet  += "dsMenuListValue=dsMenuListValue:u ";        
    var sOutDataSet  = "dsMenuListValue=dsMenuListValue ";
    
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
	
}


