/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	
	if(dsMenuGrpValue.RowCount() >8){
		fnc_Message("상위메뉴는 9개를 초과할수 없습니다", "N", "");
	}else{
		switch ( obj.GetForm().id ) {
			case "divBtnRow" :
				dsMenuGrpValue.AddRow();
				dsMenuGrpValue.SetColumn(dsMenuGrpValue.RowCount()-1,"MN_ID","D0"+dsMenuGrpValue.RowCount()+"000000");
				dsMenuGrpValue.SetColumn(dsMenuGrpValue.RowCount()-1,"UP_MN_ID","000000000");
				break;
			case "divMenuList" :
				dsMenuListValue.AddRow();         //Record Add
				dsMenuListValue.SetColumn(dsMenuListValue.RowCount()-1,"UP_MN_ID",edGrpMnID.Text);
				dsMenuListValue.SetColumn(dsMenuListValue.RowCount()-1,"UPMNNM",edGrpMnNm.Text);
				break;			
		}
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	switch ( obj.GetForm().id ) {
		case "divBtnRow" :
			if ( !fnc_DeleteCheck(dsMenuGrpValue, "chk", "1") ) return;
			break;  	
		case "divMenuList" :
			if ( !fnc_DeleteCheck(dsMenuListValue, "chk", "1") ) return;
			break;            
    }
}

function edMenuId_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
    if (   (nChar >= 48       &&  nChar <= 57)                  // 숫자
        || (nChar >= 65       &&  nChar <= 90)                  // a-z, A-Z
        || (nChar >= 97       &&  nChar <= 122)
        ||  nChar == 8 ) return true;                           // backspace
    else return false;	
}

function edMenuName_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
    if (   (nChar >= 33       &&  nChar <= 47)                  // 특수문자
        || (nChar >= 58       &&  nChar <= 64)                  
        || (nChar >= 91       &&  nChar <= 96)        
        || (nChar >= 123      &&  nChar <= 126) ) return false;
    else return true;	
}

function edUrl_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
    // : .을 제외한 특수문자
    if (   (nChar >= 33       &&  nChar <= 45)
        || (nChar >= 59       &&  nChar <= 64)        
        || (nChar >= 91       &&  nChar <= 96)        
        || (nChar >= 123      &&  nChar <= 126
        || nChar == 47) ) return false;
    else return true;		
}

function dsMenuGrpValue_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID == "MN_ID" ) {
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
			fnc_Message(L_COM_ALT_0016, "N", "메뉴그룹ID가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
			fnc_Message(L_COM_ALT_0016, "N", "메뉴그룹ID가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	}
}

function dsMenuListValue_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID == "MN_ID" ) {
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
			fnc_Message(L_COM_ALT_0016, "N", "메뉴ID가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
			fnc_Message(L_COM_ALT_0016, "N", "메뉴ID가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	}	
}

function dsMenuListValue_OnRowPosChanged(obj,nOldRow,nRow)
{
	if ( dsMenuListValue.GetRowType(nRow) == "insert" ) {
        edMenuId.Readonly = false;
	} else {
        edMenuId.Readonly = true;
	}
	
	fnc_SetBKColor(this);
}

