/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsAuthListValue) ) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	
	return true;
}



function fcSave(obj) {
    if ( !fcSaveCheck(obj) ) return;

    var sServiceName = "d01000004-service:saveUserMenu";
    var sInDataSet   = "dsUpAuthListValue=dsUpAuthListValue:u ";
    var sOutDataSet  = "dsAuthListValue=dsAuthListValue ";
    
    fcd_AddParam("USER_ID", dsUserListValue.getColumn(grdUserList.clickedRow, "USER_ID"));
    
    fcd_AddParam("INST_ID", gdsUser.getColumn(0, "USER_ID"));
    fcd_AddParam("UPDT_ID", gdsUser.getColumn(0, "USER_ID"));

    var sFindRow = "";
    var sParentID = "";
/*    
    if( dsUserListValue.getColumn(dsUserListValue.currow, "CHK") != 1 ) {
        dsUserListValue.setColumn(dsUserListValue.currow, "CHK", 1);
    }
*/    
    for ( var i = 0; i < dsAuthListValue.rowcount; i++ ) {        
        if(    dsAuthListValue.getColumn(i, "SRCH_YN") == 1
            || dsAuthListValue.getColumn(i, "INPT_YN") == 1 ) {
            
            if(dsAuthListValue.getColumn(i, "SRCH_YN") != 1) dsAuthListValue.setColumn(i, "SRCH_YN", 0);
            if(dsAuthListValue.getColumn(i, "INPT_YN") != 1) dsAuthListValue.setColumn(i, "INPT_YN", 0);
            
            sFindRow = dsUpAuthListValue.AddRow();
            dsUpAuthListValue.setColumn(sFindRow, "MN_ID", dsAuthListValue.getColumn(i, "MN_ID"));
            if(dsAuthListValue.getColumn(i, "INPT_YN") == 1) {
                dsUpAuthListValue.setColumn(sFindRow, "SRCH_YN",    1);
            } else {
                dsUpAuthListValue.setColumn(sFindRow, "SRCH_YN",    dsAuthListValue.getColumn(i, "SRCH_YN"));
            }
            
            dsUpAuthListValue.setColumn(sFindRow, "INPT_YN",   dsAuthListValue.getColumn(i, "INPT_YN"));
                        
            sParentID = substr(dsAuthListValue.getColumn(i, "MN_ID"), 0, 3)+'000000';
            sFindRow = dsUpAuthListValue.findrow("MN_ID", sParentID);
            // 대메뉴 ID가 존재하지 않으면...
            if(sFindRow == -1) {
                sFindRow = dsUpAuthListValue.AddRow();
                dsUpAuthListValue.setColumn(sFindRow, "MN_ID", sParentID);
                dsUpAuthListValue.setColumn(sFindRow, "SRCH_YN", 1);
                dsUpAuthListValue.setColumn(sFindRow, "INPT_YN", 1);                
            }
        }
    }
    
    
//    fnc_DatasetPrint(dsUpAuthListValue);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


