/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
	
	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        fcSearch();
	}	
	
	// 조회 완료 후
	if ( sServiceName == "d01000002-service:searchUser" ) {
        dsUserListValue.AddColumn("CHK");
        
        dsAuthListValue.ClearData();
        fcGetAuthMenuList(dsUserListValue.getColumn(0, "USER_ID"));
	}
	
	// 메뉴조회 완료 후 
	if ( sServiceName == "d01000004-service:searchUserMenu" ) {
        dsAuthListValue.addColumn("CHK");
    }
    
    
    // 저장 완료 후 
	if ( sServiceName == "d01000004-service:saveUserMenu" ) {
        dsUpAuthListValue.ClearData();    }
    
    if ( sServiceName = "d01000003-service:searchGrpMenu" ) {
        var idx = dsMenuGrpValue.addRow();
        dsMenuGrpValue.setColumn(idx, "MN_ID", "000000000");
        dsMenuGrpValue.setColumn(idx, "MN_NM", "전체");
        
        dsMenuGrpValue.Sort("MN_ID");
    }
    
    if ( sServiceName = "d01000004-service:searchTeamNm" ) {
        var idx = dsOutTeamNm.addRow();      
        dsOutTeamNm.SetColumn(idx, "TEAM_NM", "전체");
        dsOutTeamNm.SetColumn(idx, "RR", "0");
        dsOutTeamNm.Sort("RR");
        cboTeamNm.Index = 0;
    }
    
}

