function fcGetAuthMenuList(userId) {
    var sServiceName = "d01000004-service:searchUserMenu";
    var sInDataSet   = "";
    var sOutDataSet  = "dsAuthListValue=dsAuthListValue";
    
    fcd_AddParam("USER_ID", userId);
    
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);     
}

function fcAuthMenuListBefore(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    var sIndex = grdUserList.clickedRow;
    if( nCell != 0 ) fcGetAuthMenuList(dsUserListValue.getColumn(sIndex, "USER_ID"));
}


function dsAuthListValue_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
    if(fnc_isNull(varOldValue) == null) varOldValue = "1";
    var xx = 0;
    var sChk = substr(dsAuthListValue.getColumn(grdAuthList.clickedRow, "MN_ID"), 0, 3);

	if ( strColumnID == "CHK" ) {
        for ( xx = 0; xx <= dsAuthListValue.rowcount; xx++) {
            if(substr(dsAuthListValue.getColumn(xx, "MN_ID"), 0, 3) == sChk) {
                dsAuthListValue.setColumn(xx, "SRCH_YN", varNewValue);
                dsAuthListValue.setColumn(xx, "INPT_YN",   varNewValue);
            }
        }
	}
}


function cboGrpSel_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	for ( var i = 0; i < dsAuthListValue.rowcount; i++ ) {
        if(cboGrpSel.Index == 0) {
            dsAuthListValue.setColumn(i, "SRCH_YN", chkSrch.Value);
            dsAuthListValue.setColumn(i, "INPT_YN", chkInpt.Value);          
        } else {
            if(substr(strCode, 0, 3) == substr( dsAuthListValue.getColumn(i, "MN_ID"), 0, 3)) {
                dsAuthListValue.setColumn(i, "SRCH_YN", chkSrch.Value);
                dsAuthListValue.setColumn(i, "INPT_YN", chkInpt.Value);            
            } 
        }
	}
}


function chk_OnClick(obj,strValue)
{
	for ( var i = 0; i < dsAuthListValue.rowcount; i++ ) {
        if(cboGrpSel.Index == 0) {
            dsAuthListValue.setColumn(i, "SRCH_YN", chkSrch.Value);
            dsAuthListValue.setColumn(i, "INPT_YN", chkInpt.Value);          
        } else {
            if(substr(cboGrpSel.Value, 0, 3) == substr( dsAuthListValue.getColumn(i, "MN_ID"), 0, 3)) {
                dsAuthListValue.setColumn(i, "SRCH_YN", chkSrch.Value);
                dsAuthListValue.setColumn(i, "INPT_YN", chkInpt.Value);            
            } 
        }
	}	
}
