    }    
}
/*-------------------------------------------------------------------------------------------------+
>>  확정 여부 조회
+-------------------------------------------------------------------------------------------------*/

function fcCfm() {
          
    var sServiceName  = "d02000001-service:searchCfm";
    var sInDataSet    = "";
    var sOutDataSet   = " dsCfm=dsCfm ";

	fcd_AddParam(Trim("racerPerioNo"), cbPerioNo.Value);
	fcd_AddParam(Trim("mn_id"), sFormId);	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
/*
조회시 기수check
*/
function fcSearchCheck(obj) {

	fcInit(obj);
	
	if(fnc_IsNull(cbPerioNo.Value)) {
        // 기수 누락
        fnc_Message(SNIS_CAN_0008, "N", "기수");
        return false;
    }
	return true;
}
/*
조회
*/
function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;
 	        
    var sServiceName = "d02000001-service:searchWkStandard";
    var sInDataSet   = "";
    var sOutDataSet  = " dsOutWkStandard=dsOutWkStandard ";
         	  	
	fcd_AddParam(Trim("racerPerioNo"), Trim(cbPerioNo.Value));
	fcd_AddParam(Trim("yy"), Trim(cbYear.Value));
	fcd_AddParam(Trim("mm"), Trim(cbMonth.Value));		
		
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {

    if((dsCfm.GetColumn(0,"CFM_FG")) == "Y"){      		 // 확정여부 check
		fnc_Message(SNIS_CAN_0012, "N", "주단위기준");   // 확정시 message 출력
		return false;
    }
	if ( !fnc_DatasetChangeCheck(dsOutWkStandard) ) 	//변경된 자료가 있는지 체크
	{
		fnc_Message(SNIS_COM_1006, "N");		   		//저장 할 자료가 없음
		return false;
	}
	  
	oDataSet = null;
	sMandatoryColID   = null;
	sMandatoryColName = null;
	oGrid             = null;
	
	oDataSet = dsOutWkStandard;
	sMandatoryColID   = "WK|STR_DT|END_DT";
	sMandatoryColName = "주차|시작일자|종료일자";
	oGrid             = grdWkStandard;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	
	// 주차 중복
	if( fnc_IsColumnDuplicate(dsOutWkStandard, "WK") ) {
        
        fnc_Message(SNIS_CAN_0009, "N", "주차");
        return false;
	}
	
	// 시작일자 중복 체크
	if( fnc_IsColumnDuplicate(dsOutWkStandard, "STR_DT") ) {
        
        fnc_Message(SNIS_CAN_0009, "N", "시작일자");
        return false;
	}
	 
	// 종료일자 중복 체크 
	if( fnc_IsColumnDuplicate(dsOutWkStandard, "END_DT") ) {
        
        fnc_Message(SNIS_CAN_0009, "N", "종료일자");
        return false;
	}
	
	// 기수 입력항이 한자리일 경우
	if( Bound(meInPerioNo.Value) == 1 ) {
	
       meInPerioNo.Value = Trim("0" + meInPerioNo.Value);
	}
	
	// 년도 입력항이 4자리가 아닐 경우
	if( Bound(meInYear.Value) != 4 ) {
	}
	
	return true;
}

// 저장 공통
function fcSave(obj) {
  
   if ( !fcSaveCheck() ) return;
     
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
       
    sServiceName  = "d02000001-service:saveWkStandard";
    
    sInDataSet    = " dsOutWkStandard=dsOutWkStandard:u " ;
    sOutDataSet   = " dsOutWkStandard=dsOutWkStandard " ;

   for(var i=0; i<dsOutWkStandard.rowcount; i++) {   
		dsOutWkStandard.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value)) ;
		dsOutWkStandard.SetColumn(i, "YY", SubStr(dsOutWkStandard.GetColumn(i,"STR_DT"), 0, 4)) ;
		dsOutWkStandard.SetColumn(i, "MM", "0" + SubStr(dsOutWkStandard.GetColumn(i,"STR_DT"), 4, 2)) ;
    }
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


