function fcSetCombo() { // 년도 조회

    var sServiceName  = "d02000001-service:searchPerioNo";
    var sInDataSet    = "";
    var sOutDataSet   = " dsPerioNo=dsPerioNo ";
        sOutDataSet  += " dsYear=dsYear ";
               
