/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
// 조회 check
function fcSearchCheck(obj) {

	fcInit(obj);
	
	if((fnc_IsNull(cbRacerPeriodNo.Value)) || fnc_IsNull(cbRacerPeriodNo.value)) {
      
		fnc_Message(SNIS_CAN_0008, "N");		//조회할 자료가 없음
		return false;
	}
	
	return true;
}
// 조회
function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;
    
    var sServiceName = "d02000002-service:searchEduTotal";
    var sInDataSet   = "";
    var sOutDataSet  = " dsOutEduOutl=dsOutEduOutl dsOutEduAssign=dsOutEduAssign dsDetlEduAssign=dsDetlEduAssign dsPersonNo=dsPersonNo ";
      	  	
	fcd_AddParam("racerPerioNo", Trim(cbRacerPeriodNo.Value));
	fcd_AddParam("newCrsCd", Trim(cbCrsGbn.Value));		
	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
} 


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {

    if((dsCfm.GetColumn(0,"CFM_FG")) == "Y"){
		fnc_Message(SNIS_CAN_0012, "N", "종합교육일정");
		return false;
    }
	//변경된 자료가 있는지 체크
	if ( 
         !fnc_DatasetChangeCheck(dsOutEduOutl) && 
         !fnc_DatasetChangeCheck(dsOutEduAssign) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	
	// 기수 체크
	switch(cbPerioNo.Text) {
        
        case null :
        case ""   :
        case " "  :
        case "  " :
                    // 기수 입력항목 체크
                    fnc_Message(SNIS_COM_1035, "N", "기수 는");
                    return;
            break;
        default   :
                        
            break;
    }
    
    // 교육개요 항목 체크
    if(
        (calEduStartNoRacer.Value == null) || (calEduEndNoRacer.Value == null) 
        || (edMmNoRacer.Value == null) || (meTotalEduNoRacer.Value == null) 
        || (meEduNumNoRacer.Value == null) || (calEduStartRacer.Value == null) 
        || (calEduEndRacer.Value == null) || (edMmRacer.Value == null) 
        || (meTotalEduRacer.Value == null) || (meEduNumRacer.Value == null)
        || (calEduStartCommon.Value == null) || (calEduEndCommon.Value == null) 
        || (edMmCommon.Value == null) || (meTotalEduCommon.Value == null) 
        || (meEduNumCommon.Value == null)
    ) {
        fnc_Message(SNIS_CAN_0010, "N", "교육개요 데이터");	
		return false;
    }
    
    // 시간배정표 항목 체크
	oDataSet = null;
	sMandatoryColID   = null;
	sMandatoryColName = null;
	oGrid             = null;
	
	oDataSet = dsOutEduAssign;
	sMandatoryColID   = "SECTN_CD|CRS_CD|RACER_TIME|LECTR|BLNG";
	sMandatoryColName = "구분코드|과목코드|교육시간(선수)|강사|소속";
	oGrid             = grdEduTime;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
		
	// 시간배정표 과목 중복입력 체크
	if(fnc_IsColumnDuplicate(dsOutEduAssign, "CRS_CD")) {
	
        fnc_Message(SNIS_CAN_0009, "N", "과목이");	
		return false;    
	}

	return true;
}

// 저장전 교육개요 DataSet 채우기
function fcSaveSet() {
    
    if(Bound(cbPerioNo.Value) == 1) {
    
        cbPerioNo.Value = "0" + Trim(cbPerioNo.Value);
    }
    
    dsOutEduOutl.ClearData();
    for(var i=0; i<3; i++) {
      
         dsOutEduOutl.AddRow();
         if(i == 0) {

                dsOutEduOutl.SetColumn(i, "RACER_PERIO_NO", Trim(cbRacerPeriodNo.Value));
                dsOutEduOutl.SetColumn(i, "GBN", "0");
                dsOutEduOutl.SetColumn(i, "EDU_STR_DT",   Trim(calEduStartNoRacer.Value));
                dsOutEduOutl.SetColumn(i, "EDU_END_DT",   Trim(calEduEndNoRacer.Value));
                dsOutEduOutl.SetColumn(i, "EDU_MM",       (Trim(edMmNoRacer.Value)));
                dsOutEduOutl.SetColumn(i, "TOT_EDU_TIME", (Trim(meTotalEduNoRacer.Value)));
          }
          else if(i == 1) {
                
                dsOutEduOutl.SetColumn(i, "RACER_PERIO_NO", Trim(cbRacerPeriodNo.Value));
                dsOutEduOutl.SetColumn(i, "GBN", "1");
                dsOutEduOutl.SetColumn(i, "EDU_STR_DT",   Trim(calEduStartRacer.Value));
                dsOutEduOutl.SetColumn(i, "EDU_END_DT",   Trim(calEduEndRacer.Value));
                dsOutEduOutl.SetColumn(i, "EDU_MM",       (Trim(edMmRacer.Value)));
                dsOutEduOutl.SetColumn(i, "TOT_EDU_TIME", (Trim(meTotalEduRacer.Value)));          
          }
          else {
        
                dsOutEduOutl.SetColumn(i, "RACER_PERIO_NO", Trim(cbRacerPeriodNo.Value));
                dsOutEduOutl.SetColumn(i, "GBN", "2");
                dsOutEduOutl.SetColumn(i, "EDU_STR_DT",   Trim(calEduStartCommon.Value));
                dsOutEduOutl.SetColumn(i, "EDU_END_DT",   Trim(calEduEndCommon.Value));
                dsOutEduOutl.SetColumn(i, "EDU_MM",       (Trim(edMmCommon.Value)));
                dsOutEduOutl.SetColumn(i, "TOT_EDU_TIME", (Trim(meTotalEduCommon.Value)));
          }
    }
    
    for(var i=0; i<dsOutEduAssign.rowcount; i++) {
        
        dsOutEduAssign.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
    }
}

// 저장 공통
function fcSave(obj) {
    
   if ( !fcSaveCheck() ) return;
    
    fcSaveSet();
           
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
       
    sServiceName  = "d02000002-service:saveEduOutl";
    sInDataSet    += " dsOutEduAssign=dsOutEduAssign:u " ;
    sOutDataSet   += " dsOutEduAssign=dsOutEduAssign " ;
      
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


