/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
    switch(sServiceName) {
    
        // 공통코드 조회시
        case "common-service:searchCode" :
            dsGbn.InsertRow(0); 
            dsGbn.SetColumn(0, "CD_NM", "전체"); 
       	    btnCfm.Enable = false;
            btnCfmCls.Enable = false;   
            cbCrsGbn.Index = 0;
	        break;
	        
        //  기수조회 트랜젝션후 콜백 처리    
        case "d02000002-service:searchPerioNo" :
              //fnc_DatasetPrint(dsGudLectr);  
              fcSearch();                
            break;
        
        // 검색조건으로 조회했을 경우
        case "d02000002-service:searchEduTotal" :
            
            cbPerioNo.Text = dsOutEduOutl.GetColumn(0, "RACER_PERIO_NO");          
            // 교육개요 출력
            for(var i=0; i<dsOutEduOutl.rowcount; i++) {
            
                if(dsOutEduOutl.GetColumn(i, "GBN") == 0) {
                
                   calEduStartNoRacer.Value = dsOutEduOutl.GetColumn(i, "EDU_STR_DT");
                   calEduEndNoRacer.Value   = dsOutEduOutl.GetColumn(i, "EDU_END_DT");
                   edMmNoRacer.Text         = dsOutEduOutl.GetColumn(i, "EDU_MM");
                   meTotalEduNoRacer.Text   = dsOutEduOutl.GetColumn(i, "TOT_EDU_TIME");
                   meEduNumNoRacer.Text     = dsPersonNo.GetColumn(i, "EDU_PERS_NO");
                }
                else if(dsOutEduOutl.GetColumn(i, "GBN") == 1) {
                
                   calEduStartRacer.Value = dsOutEduOutl.GetColumn(i, "EDU_STR_DT");
                   calEduEndRacer.Value   = dsOutEduOutl.GetColumn(i, "EDU_END_DT");
                   edMmRacer.Text         = dsOutEduOutl.GetColumn(i, "EDU_MM");
                   meTotalEduRacer.Text   = dsOutEduOutl.GetColumn(i, "TOT_EDU_TIME");
                   meEduNumRacer.Text     = dsPersonNo.GetColumn(i, "EDU_PERS_NO");
                }
                else if(dsOutEduOutl.GetColumn(i, "GBN") == 2) {
                
                   calEduStart.Text        = dsOutEduOutl.GetColumn(i, "EDU_STR_DT");
                   calEduEnd.Text          = dsOutEduOutl.GetColumn(i, "EDU_END_DT");
                   edMmCommon.Text         = dsOutEduOutl.GetColumn(i, "EDU_MM");
                   meTotalEduCommon.Text   = dsOutEduOutl.GetColumn(i, "TOT_EDU_TIME");
                   meEduNumCommon.Text     = dsPersonNo.GetColumn(i, "EDU_PERS_NO");
                }
            }
            
            fcSetGridStat();         
            fcCfm();         
            break;
        case "d02000002-service:searchCfm" :
            if(dsCfm.RowCount() == 0){ 
				fcCfmInsert();
            }
            if((dsCfm.GetColumn(0,"CFM_FG")) == "Y"){
                btnCfm.Enable = false;
            	btnCfmCls.Enable = true;
            }else{
            	btnCfm.Enable = true;
            	btnCfmCls.Enable = false;
            }
            //fnc_DatasetPrint(dsCfm);	
             break;
        case "d02000002-service:saveCfm" :
			fcSearch();
			break;
        case "d02000002-service:saveEduOutl" :  
                //fnc_DatasetPrint(dsOutEduAssign);
             //   fcSetCombo();
                             
            break;
                       
        default :
            break;
    }
    
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

