function fcAddRow(obj) {
	
	//DataSet의 Header 정보가 있을 때  Add 처리
	if ( obj.GetForm().id == "divBtnRow" ) {
       
       dsOutEduAssign.AddRow();
       dsOutEduAssign.SetColumn(dsOutEduAssign.lastrow, "GRIDSTAT", "1");
    }        
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( obj.GetForm().id == "divBtnRow" ) {
            var sArg = "";

			if ( !fnc_DeleteCheck(dsOutEduAssign, "chk", "1") ) return;
    }
}

/*-------------------------------------------------------------------------------------------------+
>>  각 그리드의 레코드 상태 표시 초기화(편집 이전)
+-------------------------------------------------------------------------------------------------*/
function fcSetGridStat() {
    dsOutEduAssign.AddColumn("GRIDSTAT");
        for(var i=0; i<dsOutEduAssign.rowcount; i++) {
            dsOutEduAssign.SetColumn(i, "GRIDSTAT", "0");
    }
}

