function fcCfmInsert(){

	dsCfm.ClearData();
	dsCfm.AddRow();	
	dsCfm.SetColumn(0,"RACER_PERIO_NO",cbPerioNo.Value);
	dsCfm.SetColumn(0,"YY","0000");
	dsCfm.SetColumn(0,"MN_ID",sFormId );
}
function fcCfm() {
          
    var sServiceName  = "d02000002-service:searchCfm";
    var sInDataSet    = "";
    var sOutDataSet   = " dsCfm=dsCfm ";
   // alert (cbPerioNo.Value+"/"+cbYear.Value+"/"+sFormId);
	
	fcd_AddParam(Trim("racerPerioNo"), cbPerioNo.Value);
	fcd_AddParam(Trim("mn_id"), sFormId);
	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	
}
