function dsOutEduAssign_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "SECTN_CD"){	 
		var sectnCd = dsOutEduAssign.GetColumn(nRow,"SECTN_CD");
		  
		switch(sectnCd){
			case "001" : dsCrs.Filter("GRP_CD='D10030'"); 	break;		
			case "002" : dsCrs.Filter("GRP_CD='D10064'");	break;			
			case "003" : dsCrs.Filter("GRP_CD='D10088'");	break;
			case "004" : dsCrs.Filter("GRP_CD='D10089'");	break;
			case "005" : dsCrs.Filter("GRP_CD='D10090'");	break;
		}
	}
}

function grdEduTime_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nCell == 2){
		if (fnc_IsNull(dsOutEduAssign.GetColumn(nRow,"SECTN_CD"))){
			fnc_Message(SNIS_CAN_J003, "N", "");
			grdEduTime.SetCellPos(1);
		    return false;
		}
	}

}



function dsOutEduAssign_OnRowPosChanged(obj,nOldRow,nRow)
{
	var sectnCd = dsOutEduAssign.GetColumn(nRow,"SECTN_CD");
		  
	switch(sectnCd){
		case "001" : dsCrs.Filter("GRP_CD='D10030'"); 	break;		
		case "002" : dsCrs.Filter("GRP_CD='D10064'");	break;			
		case "003" : dsCrs.Filter("GRP_CD='D10088'");	break;
		case "004" : dsCrs.Filter("GRP_CD='D10089'");	break;
		case "005" : dsCrs.Filter("GRP_CD='D10090'");	break;
	}
}
