/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
 
    if((dsCfm.GetColumn(0,"CFM_FG")) == "Y"){
		fnc_Message(SNIS_CAN_0012, "N", "세부교육일정");
		return false;
    }
    
	if ( !fnc_DatasetChangeCheck(dsDetlEduAssign) ) 
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return;
	}
	
	// 필수항목체크
	oDataSetDetlEduAssign = dsDetlEduAssign;

	sMandatoryColID   = null;
	sMandatoryColName = null;
	oGrid             = null;
	
	sMandatoryColID   = "DT|STR_END_TIME|SECTN_CD|CRS_CD|DETL_EDU_CRS|OBJ|LECTR|PLC|EDU_TIME";
	sMandatoryColName = "일짜|시작및종료시간|학과_실기코드|과목코드|세부교육과목|대상|강사|장소|교육시간";
	oGrid             = grdDetlEduAssign;
	if ( fnc_GetMandatoryViolateRow(oDataSetDetlEduAssign, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	
	return true;
}


function fcSave(obj) {
   
   if ( !fcSaveCheck() ) return;
    
//    fcSaveSetting();
           
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
       
    sServiceName  = "d02000003-service:saveEduDetail";

    sInDataSet    = "dsDetlEduAssign=dsDetlEduAssign:u ";
    sOutDataSet   = "dsDetlEduAssign=dsDetlEduAssign ";
      
    for(var i=0; i<dsDetlEduAssign.rowcount; i++) {
	//	trace("mm===================>"+Trim(cbMonth.Value));      
        dsDetlEduAssign.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
    }
    
 //   fcd_AddParam("racerPeriodNo", cbRacerPeriodNo.GetSelText());
       
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
    
    
}


