/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
	switch(sServiceName) {
	    case "d02000003-service:searchUser" :
			fcSearch1();
			break;
		case "d02000003-service:searchYear" :
			cbYear.Index = 0;
			break;
		case "d02000003-service:searchPerioNo" :
		        btnCfm.Enable = false;
				btnCfmCls.Enable = false; 
				break;	
		case "d02000003-service:searchEduDetail" :
				fcCfm();
				break;
		case "d02000003-service:searchCfm" :
            if(dsCfm.RowCount() == 0){ 
				fcCfmInsert();
            }
            if((dsCfm.GetColumn(0,"CFM_FG")) == "Y"){
                btnCfm.Enable = false;
            	btnCfmCls.Enable = true;
            }else{
            	btnCfm.Enable = true;
            	btnCfmCls.Enable = false;
            }
           // fnc_DatasetPrint(dsCfm);	
             break;
        case "d02000003-service:saveCfm" : 
			fcUser();
			break;
	}
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {
}

