}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {
	if(cbWeekPart.Value == null){
		fnc_Message("주차가 전체 조회 일때는 저장할 수 없습니다.", "N", "");
		return;
	}
	
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsWkSche) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	
	// 기수 체크
	switch(cbPerioNo.Text) {
        
        case null :
        case ""   :
        case " "  :
        case "  " : fnc_Message(SNIS_COM_1035, "N", "기수");
                    return;
            break;
        default   :
            break;
    }
        
    // 주차 체크
	switch(cbWeekPart.Text) {
        
        case null :
        case ""   :
        case " "  :
        case "  " : fnc_Message(SNIS_COM_1035, "N", "주차");
                    return;
            break;
        default   :
            break;
    }
    
    // 교육일정표 항목 체크
	oDataSet = null;
	sMandatoryColID   = null;
	sMandatoryColName = null;
	oGrid             = null;
	
	oDataSet = dsWkSche;
	sMandatoryColID   = "DT|STR_END_TIME|SECTN_CD|CRS_CD|DETL_EDU_CRS|OBJ|LECTR|PLC|TIME";
	sMandatoryColName = "날짜|시작및 종료 시간|구분|과목|세부내용|대상|강사|장소|시간";
	oGrid             = grdWkSche;
	
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}


	// 일자 시간 동시 중복 체크
	for(var i=0; i<dsWkSche.rowcount; i++) {
        var orignDt     = dsWkSche.GetColumn(i, "DT");
        var orignStTime = dsWkSche.GetColumn(i, "STR_END_TIME");
        
        for(var j=0; j<dsWkSche.rowcount; j++) {
            var compareDt     = dsWkSche.GetColumn(j, "DT");
            var compareStTime = dsWkSche.GetColumn(j, "STR_END_TIME");
            
            if((i != j) && (orignDt == compareDt) && (orignStTime == compareStTime)) {
                fnc_Message(SNIS_CAN_0009, "N", (i+1)+"번째와 "+(j+1)+"번째의 일자, 시작및종료시간");
                return false;
            }
        }
	}
	
	return true;
}


function fcSaveSet() {
    // 기수 자리수 조정
    if(Bound(cbPerioNo.Value) == 1) {
    
        cbPerioNo.Value = "0" + Trim(cbPerioNo.Value);
    }
}
// 저장 공통
function fcSave(obj) {
        
    if ( !fcSaveCheck() ) return;   
    fcSaveSet();
     //alert( comPerioNo +"/"+  comYear + "/" + comMonth +"/" + comWeek);
    // 기수, 년도, 월, 주차 채우기
    for(var i=0; i<dsWkSche.rowcount; i++) {
        dsWkSche.SetColumn(i,"RACER_PERIO_NO", cbPerioNo.Value);
     //   dsWkSche.SetColumn(i,"YY", cbYearList.Value);
     //   dsWkSche.SetColumn(i,"MM", cbMonthList.Value);
        dsWkSche.SetColumn(i,"WK", cbWeekPart.Value);
        dsWkSche.SetColumn(i, "SEQ", dsWkSche.GetColumn(i,"SEQ"));
    }
     
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
       
    sServiceName  = "d02000004-service:saveEduWeek";
    
    sInDataSet     = " dsWkSche=dsWkSche:u " ;
    sOutDataSet    = " dsWkSche=dsWkSche " ;
    // fnc_DatasetPrint(dsWkSche);    
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


