/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
    switch(sServiceName) {
    
        //  기수조회 트랜젝션후 콜백 처리    
        case "d02000004-service:searchNoYear" :
			dsWeek.InsertRow(0); 
            dsWeek.SetColumn(0, "CD_NM", "전체"); 
			cbPerioNo.Index =0;
            cbWeekPart.Index = 0;
            grdWkSche.Editable = false;
         //   searchWk();
            break;
        case "d02000004-service:searchUser" :
			fcSearch1(); 
			break;
        // 주차구간을 조회했을 경우
        case "d02000004-service:searchWkPart" :
			fcSearchCurEduStat();
            break;
        
        // 교육개요 조회했을 경우 
        case "d02000004-service:searchEduTotal" :
        	//fnc_DatasetPrint(dsCurEduStat);
			fcUser();  
			fcWkPartSet();
            break;        
        
        //  주간교육일정을 조회했을 경우 
        case "d02000004-service:searchWkSche":
            // fcSearchCurEduStat();       //1   
             
            break;
            
        case "d02000004-service:searchWk" :
        
            break;
            
        case "d02000004-service:saveEduWeek" :
                
            break;
        case "d02000004-service:searchPrint"  :
			fcPrintDetail();
            break;
        case "d02000004-service:searchWkScheCopy" :

			if(dsWkSche.RowCount() == 0) {
				fnc_Message(SNIS_CAN_J005, "N", "11111");
				return false;
            }
			for(var i=0;i < dsWkSche.RowCount();i++){
				dsWkSche.SetColumn(i,"COPY","1");
			}
            break;
        default :
            break;
    }
    
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

