function fcSearchCurEduStat() {

    var sServiceName = "d02000004-service:searchEduTotal";
    var sInDataSet    = "";
    var sOutDataSet   = " dsCurEduStat=dsCurEduStat dsOutEduAssign=dsOutEduAssign dsOutCurEduTm=dsOutCurEduTm dsListNum=dsListNum";

    fcd_AddParam("endDate", dsWkPart.GetColumn(dsWkPart.rowcount-1, "END_DT"));
	fcd_AddParam("rPerioNo", cbPerioNo.Value);
	fcd_AddParam("yYyearVal", cbYearList.Value);
	fcd_AddParam("mMmonthVal", cbMonthList.Value);
	fcd_AddParam("wKweekPart", cbWeekPart.Value);
	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	//fnc_DataSetPrint(dsListNum);
}

