    fcd_AddParam("rPerioNo", cbPerioNo.Value);
  //  fcd_AddParam("yYyearVal", cbYearList.Value);
//	fcd_AddParam("mMmonthVal", cbMonthList.Value);
	fcd_AddParam("wKweekPart", cbWeekPart.Value); 

                            
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
