/*-------------------------------------------------------------------------------------------------+
>>  Clear 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	if ( obj.id == "btnCancel" || obj.id == "btnSearch") {
		cbRacerPerioNo.Index = -1;
		dsSeltReptList.ClearData();
		dsSeltReptDesc.ClearData();
		
		dsFamList.ClearData();
		dsEduList.ClearData();
		dsActList.ClearData();
		dsCarList.ClearData();
		dsLicList.ClearData();
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj){
    if(fnc_IsNull(cbRacerPerioNo.Value)){
		fnc_Message(SNIS_CAN_0008, "N","기수");
		return;
	}

    var sServiceName = "d02000005-service:searchSeltRept";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSeltReptList=dsSeltReptList";

	fcd_AddParam("RACER_PERIO_NO", Trim(cbRacerPerioNo.Value));
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false, "S");
}

