function fcSaveProc(nCandNo){
	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	if(Length(nCandNo)>0){//키가 넘어 왔을 경우에만 키를 셋팅. 아닐 경우는 그냥 처리.
		dsSeltReptDesc.SetColumn(0,"REPT_NO",nCandNo);
	}
	sServiceName     = "d02000005-service:saveSeltRept";    
	
	sInDataSet       = "dsSeltReptDesc=dsSeltReptDesc:A";
	sInDataSet		+= " dsFamList=dsFamList:u";
	sInDataSet		+= " dsEduList=dsEduList:u";
	sInDataSet		+= " dsActList=dsActList:u";
	sInDataSet		+= " dsCarList=dsCarList:u";
	sInDataSet		+= " dsLicList=dsLicList:u";
	sInDataSet		+= " dsUploadFileList=dsUploadFileList:u";
	
	sOutDataSet      = "";
	fnc_DatasetPrint(dsUploadFileList);
	if(dsUploadFile.GetRowCount()>0 || dsUploadFileList.GetRowCount() > 0){
		
		saveFile();//파일저장
		
		if(dsUploadFile.GetRowCount()>0){
			dsSeltReptDesc.SetColumn(0,"PTO_URL",dsUploadFile.GetColumn(0,"FILE_URL"));
		}
	}
	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false,"U");
}

function saveFile(obj){

	if( dsUploadFile.GetRowCount() > 0 ) {
		if(dsUploadFile.getColumn(0, "CHK") == "1" ) {

			var rtn_arr = fnc_FileWrite( divBtnLst.HttpFile , dsUploadFile.GetColumn(0, "FILE_URL" ), dsUploadFile.GetColumn(0, "FILE_NAME" ), "RACER");			
			var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFile.GetColumn(0, "FILE_URL" ));

			if ( rtn_arr[0] != "SUCC" ) {
				alert("image파일 업로드 에러발생");
				return false;
			}
			
			dsUploadFile.SetColumn(0, "FILE_URL"    , rtn_arr[1] );//실제 파일 경로.
			dsUploadFile.SetColumn(0, "FILE_SIZE"   , fileSize );
		} // end if
	} // end for
	
	if( dsUploadFileList.GetRowCount() > 0 ) {
		for(var i=0 ; i < dsUploadFileList.GetRowCount() ; i++){
			if(dsUploadFileList.getColumn(i, "CHK") == "1" ) {
				var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
											, dsUploadFileList.GetColumn(i, "FILE_URL" )
											, dsUploadFileList.GetColumn(i, "FILE_NAME" ));
				
				var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFileList.GetColumn(i, "FILE_URL" ));
				//alert("2_rtn_arr[0]"+rtn_arr[0]);
				if ( rtn_arr[0] != "SUCC" ) {
					alert("첨부파일 업로드 에러발생");
					return false;
				}
				
				dsUploadFileList.SetColumn(i, "FILE_URL"    , rtn_arr[1] );//실제 파일 경로.
				dsUploadFileList.SetColumn(i, "FILE_SIZE"   , fileSize );
			} // end if
		}
	} // end for 
}

