/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG){
    if(!fnc_result(ErrorCode, ErrorMSG) ) return;
    switch(sServiceName){
        // 공통코드 조회시
        case "d02000005-service:searchPerioNo" :
			cbRacerPerioNo.Index = 0;
			break;
        case "d02000005-service:saveSeltRept"  :  //후보생선발접수 저장
			break;
		case "d02000005-service:"://후보생 정보 조회
			//MaskEdit4.Readonly = true;
			break;
		case "d02000005-service:keyCandIdent":
			fcSaveProc(dsCandSeq.GetColumn(0,"SEQ"));
			break;
		case "d02000005-service:searchSeltReptDesc":
			//이미지 set
			var img = split(dsSeltReptDesc.GetColumn(0,"PTO_URL"),"/");
			imgPhoto.ImageID = GBL_IMAGELINK_URL+img[Length(img)-1];
			break;
    }

    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

/*-------------------------------------------------------------------------------------------------+
>>  후보생선발접수 상세 조회
+-------------------------------------------------------------------------------------------------*/
function grdSeltReptListValue_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex){
	var sServiceName     = "d02000005-service:searchSeltReptDesc";
	var sInDataSet       = "";
	var sOutDataSet      = "dsSeltReptDesc=dsSeltReptDesc";
		sOutDataSet		+= " dsFamList=dsFamList";
		sOutDataSet		+= " dsEduList=dsEduList";
		sOutDataSet		+= " dsActList=dsActList";
		sOutDataSet		+= " dsCarList=dsCarList";
		sOutDataSet		+= " dsLicList=dsLicList";
		sOutDataSet		+= " dsUploadFileList=dsUploadFileList";
	
	var sRacerPerioNo    = dsSeltReptList.getColumn(nRow,   "RACER_PERIO_NO");
	//var sResNo           = dsSeltReptList.getColumn(nRow,   "RES_NO");
	var sReptNo           = dsSeltReptList.getColumn(nRow,   "REPT_NO");
	
	//if(fnc_IsNull(sRacerPerioNo) || fnc_IsNull(sResNo)){
	if(fnc_IsNull(sRacerPerioNo) || fnc_IsNull(sReptNo) ){
		fnc_Message("선수정보 오류!","N");
		return;
	}
	
	fcd_AddParam("RACER_PERIO_NO", sRacerPerioNo);  //매개변수 : 기수
	//fcd_AddParam("RES_NO", sResNo);                //매개변수 : 주민번호
	fcd_AddParam("REPT_NO", dsSeltReptList.GetColumn(nRow,"REPT_NO"));
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

/*-------------------------------------------------------------------------------------------------+
>>  삭제 처리  
+-------------------------------------------------------------------------------------------------*/
function fcDeleteSeltRept() {
	if(dsSeltReptDesc.GetRowCount() == 0){
		fnc_Message(SNIS_COM_1003,"N");
		return false;
	}
	if(fnc_IsNull(dsSeltReptDesc.GetColumn(0,"RACER_PERIO_NO"))){
		fnc_Message("삭제할 내용이 없습니다.","N");
		return false;
	}
	 if(confirm("선택된 정보를 삭제하시겠습니까?")){
		var sServiceName = "d02000005-service:saveSeltRept";
		var sInDataSet   = "";
		var sOutDataSet  = "";
		
		fcd_AddParam("RACER_PERIO_NO", dsSeltReptDesc.GetColumn(0,"RACER_PERIO_NO")); 
		fcd_AddParam("REPT_NO", dsSeltReptDesc.GetColumn(0,"REPT_NO"));
		fcd_AddParam("RES_NO", dsSeltReptDesc.GetColumn(0,"RES_NO"));
		fcd_AddParam("DEL_YN", "Y");
		
	    //fnc_DatasetPrint(dsSeltReptDesc);
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리
	switch(obj.GetForm().id) {
	case "divBtnSelt" :
		if(dsSeltReptDesc.GetRowCount() != 0){
			dsSeltReptDesc.ClearData();
		}
		var nRow = dsSeltReptDesc.AddRow();
		dsSeltReptDesc.SetColumn(nRow,"JOIN_PATH","002");
		edSeltNo.Readonly = false;
		break;
	case "divBtnFam":
		if(isAddable()){
			var nRow = dsFamList.AddRow();
		}
		break;
	case "divBtnEdu":
		if(isAddable()){
			var nRow = dsEduList.AddRow();
		}
		break;
	case "divBtnAct":
		if(isAddable()){
			var nRow = dsActList.AddRow();
		}
		break;
	case "divBtnCar":
		if(isAddable()){
			var nRow = dsCarList.AddRow();
		}
		break;
	case "divBtnLic":
		if(isAddable()){
			var nRow = dsLicList.AddRow();
		}
		break;
	case "divBtnFile":
			fcFileSelect(obj);
		break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj){
	switch(obj.GetForm().id){
	case "divBtnSelt":
		fcDeleteSeltRept();
		break;
	case "divBtnFam":
		fcDelRowChk(dsFamList);
		break;
	case "divBtnEdu":
		fcDelRowChk(dsEduList);
		break;
	case "divBtnAct":
		fcDelRowChk(dsActList);
		break;
	case "divBtnCar":
		fcDelRowChk(dsCarList);
		break;
	case "divBtnLic":
		fcDelRowChk(dsLicList);
		break;
	case "divBtnFile":
		if ( !fnc_DeleteCheck(dsUploadFileList, "chk", "1") ) return;
		break;
	}
}

function fcDelRowChk(obj){
	if(obj.currow == -1){
		fnc_Message("선택된 행이 없습니다.","N");
		return;
	}
	else{
		obj.DeleteRow(obj.currow);
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 달력 처리
+-------------------------------------------------------------------------------------------------*/
var sCurCalId;	//현재 선택된 달력 ID
var nCurCalRow;	//현재 선택된 달력 row
var nCurCalCol;	//현재 선택된 달력 cell

function fcSelectDate(obj,nRow,nCell,strVal,nPivotIndex){
	var rect	= obj.GetCellRect(nRow, nCell);
	var vRight	= rect[2];
	var vBottom	= rect[3];
	
	var nWidth	= 0;//달력 나올 Left
	var nHeight	= 0;//달력 나올 Height
	
	sCurCalId = obj.id;
	nCurCalRow = nRow;
	nCurCalCol = nCell;
		
	switch(sCurCalId){
	case "grdFam":
		nWidth = 210;
		nHeight = 360;
		break;
	case "grdEdu":
		nWidth = 198;
		nHeight = 390;
		break;
	case "grdAct":
		nWidth = 606;
		nHeight = 390;
		break;
	case "grdCar":
		nWidth = 198;
		nHeight = 420;
		break;
	case "grdLic":
		nWidth = 506;
		nHeight = 420;
		break;
	}
	//nWidth += 12;
	//nHeight += 40;
	
	calComm.Left 	= vRight + nWidth;
	calComm.top		= vBottom + nHeight;
	
	calComm.Visible	= true;
	calComm.SetFocus();
}

function calComm_OnDayClick(obj,strText){
	calComm.Visible = false;
	
	switch(sCurCalId){
	case "grdFam":
		dsFamList.SetColumn(nCurCalRow,"BIRTH_DT",strText);
		break;
	case "grdEdu":
		if(nCurCalCol==0){
			dsEduList.SetColumn(nCurCalRow,"MTRIC_SCHL_DT",strText);
		}
		else if(nCurCalCol==2){
			dsEduList.SetColumn(nCurCalRow,"GRDU_SCHL_DT",strText);
		}
		break;
	case "grdAct":
		if(nCurCalCol==0){
			dsActList.SetColumn(nCurCalRow,"STR_DT",strText);
		}
		else if(nCurCalCol==2){
			dsActList.SetColumn(nCurCalRow,"END_DT",strText);
		}
		break;
	case "grdCar":
		if(nCurCalCol==0){
			dsCarList.SetColumn(nCurCalRow,"STR_DT",strText);
		}
		else if(nCurCalCol==2){
			dsCarList.SetColumn(nCurCalRow,"END_DT",strText);
		}
		break;
	case "grdLic":
		dsLicList.SetColumn(nCurCalRow,"REG_DT",strText);
		break;
	}
}

function calComm_OnKillFocus(obj){
	calComm.Visible = false;
}

/*-------------------------------------------------------------------------------------------------+
>>  가족사항,학력사항,주요활동,경력사항,자격면허 추가할때 체크
+-------------------------------------------------------------------------------------------------*/
function isAddable(){
	if(dsSeltReptDesc.GetRowCount() == 0){
		fnc_Message("추가할 수 없습니다.","N");
		return false;
	}
	return true;
}

function fcFileSelect(obj){
	var sResponse = fcd_GetSelectFile();
	
	if(Length(sResponse)>0){
		if(obj.id == "btPto"){
			dsUploadFile.ClearData();
			var nRow = dsUploadFile.AddRow();

			dsUploadFile.SetColumn(nRow,"CHK","1");
			dsUploadFile.SetColumn(nRow,"FILE_URL",sResponse[0] + "\\" + sResponse[1]);
			dsUploadFile.SetColumn(nRow,"FILE_NAME",sResponse[1]);
			//dsSeltReptDesc
			dsSeltReptDesc.SetColumn(nRow,"PTO_FILE",sResponse[1]);
			
			imgPhoto.ImageID = dsUploadFile.GetColumn(nRow,"FILE_URL");
		}
		else{
			var nRow = dsUploadFileList.AddRow();
	
			dsUploadFileList.SetColumn(nRow,"CHK","1");
			dsUploadFileList.SetColumn(nRow,"FILE_URL"	,sResponse[0] + "\\" + sResponse[1]);
			dsUploadFileList.SetColumn(nRow,"FILE_NAME"	,sResponse[1]);
		}
	}
}
