function fcPerioNoCopy() {
    var dsPerioNoCount 	= dsPerioNo.GetRowCount();
    var iCbPerioNo 		= eval(Trim(cbPerioNo.Value));
    var iMinusPerioNo 	= iCbPerioNo-1;
    var selectPerioNo 	= ""; //선택기수 
    var finalPerioNo 	= ""; //최종기수
   
	var sServiceName 	= "";
	var sInDataSet		= "";
	var sOutDataSet		= "";
	    
	//전기수 값 구하기
	for(var i=0; i<iCbPerioNo; i++) {
		selectPerioNo = fcPerioNoSelect(iMinusPerioNo);
		if(selectPerioNo > 0){
			finalPerioNo = selectPerioNo;
			break;
		}
		iMinusPerioNo--;  
	}
	
	if(finalPerioNo != ""){
		if(tabMain.TabIndex == 0){
		}
		else if(tabMain.TabIndex == 1){
			sServiceName = "d02000006-service:searchExamGbnPerioNoCopy";
			sInDataSet   = "";
			sOutDataSet  = " dsExamGbn=dsExamGbn ";
			fcd_AddParam("racerPerioNo", finalPerioNo);
		}else if(tabMain.TabIndex == 2){
			sServiceName = "d02000006-service:searchExamPerioNoCopy";
			sInDataSet   = "";
			sOutDataSet  = " dsExam=dsExam ";
			fcd_AddParam("racerPerioNo", finalPerioNo);	
		}else if(tabMain.TabIndex == 3){
			sServiceName = "d02000006-service:searchExamChngPerioNoCopy";
			sInDataSet   = "";
			sOutDataSet  = " dsExamChng=dsExamChng ";
			fcd_AddParam("racerPerioNo", finalPerioNo);				
		}
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
		sCopy = "Y";
	}else{
		fnc_Message(SNIS_CAN_A011, "N");
	}
}

//마이너스 한 값이 현재 기수가 있는 체크하여 있으면 기수값 리턴
function fcPerioNoSelect(str){
    var dsPerioNoCount = dsPerioNo.GetRowCount();
    var retValue = 0;
	for(var i=0; i<dsPerioNoCount; i++) {   
		selectPerioNo = dsPerioNo.GetColumn(i,"RACER_PERIO_NO");
		if(str == selectPerioNo){
			retValue = selectPerioNo;
		}
	}
	return retValue;	
}

