    var sOutDataSet  = " dsPerioNo=dsPerioNo "; 
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	var oDataSet			= "";
	var sMandatoryColID   	= "";
	var sMandatoryColName 	= "";
	var oGrid             	= "";

	if(tabMain.TabIndex == 0){
		 if((fnc_IsNull(tabMain.tab4.edFstCnt.Text))||(fnc_IsNull(tabMain.tab4.edSndCnt.Text))){
			fnc_Message(SNIS_COM_1006, "N");
				return false;
		 }
	}else if(tabMain.TabIndex == 1){
		if(sCopy == ""){	
			if ( !fnc_DatasetChangeCheck(dsExamGbn) ) 
			{
				//저장 할 자료가 없음
				fnc_Message(SNIS_COM_1006, "N");	
				return;
			}
		}
		
		// 필수항목체크
		oDataSet 			= dsExamGbn;
		sMandatoryColID 	= "EXAM_CD|ITEM_GBN_CD|FST_ASSIGN_SCR_RATE";
		sMandatoryColName 	= "시험구분|종목구분명|1차배점비율";
		oGrid				= tabMain.tab1.grdExamGbn;
	}else if(tabMain.TabIndex == 2){
		if(sCopy == ""){	
			if ( !fnc_DatasetChangeCheck(dsExam) ) 
			{
				//저장 할 자료가 없음
				fnc_Message(SNIS_COM_1006, "N");	
				return;
			}
		}
			
		// 필수항목체크
		oDataSet 			= dsExam;
		sMandatoryColID 	= "EXAM_CD|ITEM_GBN_CD|ITEM_CD|FST_ESTM_YN|ARRAY_ORD";
		sMandatoryColName 	= "시험구분|종목구분명|종목명|1차평가여부|정렬순서";
		oGrid				= tabMain.tab2.grdExam;		
	}else if(tabMain.TabIndex == 3){
		if(sCopy == ""){		
			if ( !fnc_DatasetChangeCheck(dsExamChng) ) 
			{
				//저장 할 자료가 없음
				fnc_Message(SNIS_COM_1006, "N");	
				return;
			}
		}	
		// 필수항목체크
		oDataSet 			= dsExamChng;	
		sMandatoryColID 	= "EXAM_CD|ITEM_GBN_CD|ITEM_CD|BAS_SCR";
		sMandatoryColName 	= "시험구분|종목구분명|종목명|점수";
		oGrid				= tabMain.tab3.grdExamChng;
	}    
	
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	
	return true;
}

// 조회시 처리 로직
function fcSave(obj) {
    
   if ( !fcSaveCheck() ) return;
     
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
  
    if(tabMain.TabIndex == 0){
		
		if(dsSeltCnt.GetRowCount() == 0){
			dsSeltCnt.AddRow();
			dsSeltCnt.SetColumn(0,"RACER_PERIO_NO",cbPerioNo.Value);
		}
		sServiceName  = "d02000006-service:saveSeltCnt";
		sInDataSet    = "dsSeltCnt=dsSeltCnt:u ";
		sOutDataSet   = "dsSeltCnt=dsSeltCnt ";
		
		dsSeltCnt.SetColumn(0,"CNT_FST_SELT"	,tabMain.tab4.edFstCnt.Text);
		dsSeltCnt.SetColumn(0,"CNT_SND_SELT"	,tabMain.tab4.edSndCnt.Text);
		dsSeltCnt.SetColumn(0,"DT_FST_SELT"	,tabMain.tab4.dtFstCal.Value);			
	    dsSeltCnt.SetColumn(0,"DT_FST_SELT_END" ,tabMain.tab4.dtFstCalEnd.Value);		
	    dsSeltCnt.SetColumn(0,"DT_SND_SELT"		,tabMain.tab4.dtSndCal.Value);		
	    dsSeltCnt.SetColumn(0,"DT_SND_SELT_END" ,tabMain.tab4.dtSndCalEnd.Value);	
    }
   	else if(tabMain.TabIndex == 1){
		sServiceName  = "d02000006-service:saveExamGbn";
		sInDataSet    = "dsExamGbn=dsExamGbn:u ";
		sOutDataSet   = "dsExamGbn=dsExamGbn ";
		  
		for(var i=0; i<dsExamGbn.rowcount; i++) {    
			dsExamGbn.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));		
		}
	}else if(tabMain.TabIndex == 2){
		sServiceName  = "d02000006-service:saveExam";
		sInDataSet    = "dsExam=dsExam:u ";
		sOutDataSet   = "dsExam=dsExam ";
		
		for(var i=0; i<dsExam.rowcount; i++) {    
			dsExam.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
		}		
	}else if(tabMain.TabIndex == 3){
		sServiceName  = "d02000006-service:saveExamChng";
		sInDataSet    = "dsExamChng=dsExamChng:u ";
		sOutDataSet   = "dsExamChng=dsExamChng ";
		
		for(var i=0; i<dsExamChng.rowcount; i++) {    
			dsExamChng.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));			
		}
		//trace(dsExamChng.SaveXML());
	}
	sCopy = "";
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
    
}


