/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
   
    switch(sServiceName) {
    
	case "d02000006-service:searchSeltCnt"  :
		tabMain.tab4.edFstCnt.Text 		= dsSeltCnt.GetColumn(0,"CNT_FST_SELT");
		tabMain.tab4.edSndCnt.Text 		= dsSeltCnt.GetColumn(0,"CNT_SND_SELT");
	    tabMain.tab4.dtFstCal.Value		= dsSeltCnt.GetColumn(0,"DT_FST_SELT");
	    tabMain.tab4.dtFstCalEnd.Value	= dsSeltCnt.GetColumn(0,"DT_FST_SELT_END");
	    tabMain.tab4.dtSndCal.Value		= dsSeltCnt.GetColumn(0,"DT_SND_SELT");
	    tabMain.tab4.dtSndCalEnd.Value	= dsSeltCnt.GetColumn(0,"DT_SND_SELT_END");
		fcSearchExamGbn();
		break;
	case "d02000006-service:searchExamGbn"  :
		fcSearchExam();
		break;
	case "d02000006-service:searchExam"     :
	    fcSearchExamChng();
		break;	
	case "d02000006-service:searchExamChng" :
		break;	
	default :
		break;
	}
		
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}



