/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( obj.GetForm().id == "divBtnRow" ) {
		var sArg = "";
		if(tabMain.TabIndex == 0){
        }else if(tabMain.TabIndex == 1){
			if ( !fnc_DeleteCheck(dsExamGbn, "chk", "1") ) return;
			fcInit();
//			grdEduTime.SetFocus(); //Record 삭제 후 Grid로 Focus
			dsExamGbn_OnRowPosChanged();
			break;
		}else if(tabMain.TabIndex == 2){
			if ( !fnc_DeleteCheck(dsExam, "chk", "1") ) return;
			fcInit();
			dsExam_OnRowPosChanged();
			break;
		}else if(tabMain.TabIndex == 3){
			if ( !fnc_DeleteCheck(dsExamChng, "chk", "1") ) return;
			fcInit();
			dsExamChng_OnRowPosChanged();
			break;
		}

	} 
}


function dsExamGbn_OnRowPosChanged(obj,nOldRow,nRow)
{
    if ( dsExamGbn.GetRowCount() == 0 ) return;
    
    if ( dsExamGbn.GetRowCount() <= dsExamGbn.currow ) {
        dsExamGbn.FireEvent = false;
        dsExamGbn.row = dsExamGbn.GetRowCount() - 1;
        dsExamGbn.SelectRow(dsExamGbn.GetRowCount() - 1);
        dsExamGbn.FireEvent = true;
    }
    fcInit();
}

function dsSearchExamCd_OnLoadComleted1(obj,nErrorCode,strErrorMsg,nReason)
{
	dsSearchExamCd.InsertRow(0);
	dsSearchExamCd.SetColumn(0, "CD_NM", "전체");
	cbExamCd.Index = 0;	
}

function dsSearchItemGbnCd_OnLoadComleted2(obj,nErrorCode,strErrorMsg,nReason)
{
	dsSearchItemGbnCd.InsertRow(0);
	dsSearchItemGbnCd.SetColumn(0, "CD_NM", "전체");
	cbItemGbnCd.Index = 0;	
}

function dsSearchItemCd_OnLoadComleted3(obj,nErrorCode,strErrorMsg,nReason)
{
	dsSearchItemCd.InsertRow(0);
	dsSearchItemCd.SetColumn(0, "CD_NM", "전체");
	cbItemCd.Index = 0;	
}

//조회---------------------------------------------------------------------------------------------
function cbItemCd_OnClick(obj)
{
		var strCode = cbItemGbnCd.Value;
		switch(strCode){
			case "001" : dsSearchItemCd.Filter("GRP_CD='D10063'"); 	break;		//필기
			case "003" : dsSearchItemCd.Filter("GRP_CD='D10028'");	break;      //자전거
			case "004" : dsSearchItemCd.Filter("GRP_CD='D10058'");	break;      //체력측정
			case "006" : dsSearchItemCd.Filter("GRP_CD='D10065'");	break; 
			default : break;      
		}
}

