function dsExamChng_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "ITEM_GBN_CD"){	 
		var strCode = dsExamChng.GetColumn(nRow,"ITEM_GBN_CD");
		comboCheck(strCode);
	}

}
