{
	var strCode = dsExamChng.GetColumn(nRow,"ITEM_GBN_CD");
	comboCheck(strCode);
}
//환산기준표
function grdExamChng_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	if (nCell == 4){
		if (fnc_IsNull(dsExamChng.GetColumn(nRow,"ITEM_GBN_CD"))){
				fnc_Message(SNIS_CAN_0008, "N", "종목구분");
				grdExamChng.SetCellPos(3);
				return false;
			} else {
			}
	}
}

