function comboCheck(strCode){

	switch(strCode){
		case "001" : dsInputItemCd.Filter("GRP_CD='D10063'"); 	break;		//필기
		case "003" : dsInputItemCd.Filter("GRP_CD='D10028'");	break;      //자전거
		case "004" : dsInputItemCd.Filter("GRP_CD='D10058'");	break;      //체력측정
		case "006" : dsInputItemCd.Filter("GRP_CD='D10065'");	break; 		//면접
		default : break;      
	}

}
