function fcInit(obj) {
  if ( obj.id == "btnCancel" || obj.id == "btnSearch") { 
        
         if(tabMain.TabIndex == 0){        //ÀÚÀü°Å ½Ç±â 
           dsSndSeltExam.ClearData(); 
         }
         else if(tabMain.TabIndex == 2){   //ÇÊ±â 
           dsSndSeltExam2.ClearData(); 
         }
         else if(tabMain.TabIndex == 3){   //¸éÁ¢
           dsSndSeltExam3.ClearData(); 
         }
         else if(tabMain.TabIndex == 4){   //¼ºÀûÁý°è
           dsSndSeltExam4.ClearData(); 
         }
    }    
}
