function fcSearchUseGbnItem(){

	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
    
    sServiceName  = "d02000008-service:searchUseGbnItem";
	sInDataSet    = "";
	sOutDataSet   = " dsUseGbnItem=dsUseGbnItem dsUseItem=dsUseItem dsSndExamRateBas=dsSndExamRateBas dsSeltChng=dsSeltChng dsCandNo=dsCandNo ";

	fcd_AddParam("racerPerioNo", cbRacerPerioNo.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	
}
function fcStartEndDate(){
	
	mkdate.Text = dsUseGbnItem.getColumn(0,"DT_STR")+dsUseGbnItem.getColumn(0,"DT_END");
}
/*-------------------------------------------------------------------------------------------------+
//	1. Á¾¸ñ check
//	2. »ç¿ëÇÏ´Â Á¾¸ñ¿¡ ´ëÇØ¼­ grid  µî·ÏºÎºÐ È°¼º ¹× ºñÈ°¼º 
+-------------------------------------------------------------------------------------------------*/
function fcUseEditable(){

	if (dsUseGbnItem.GetColumn(0,"A") == 0){			// ÇÊ±â
		tabMain.tab2.grdSndSeltExam2.Editable = false;
	}else{
	    tabMain.tab2.grdSndSeltExam2.Editable = true;
	}
	if (dsUseGbnItem.GetColumn(0,"D") == 0){			//Ã¼·ÂÃøÁ¤
		tabMain.tab5.grdStrTest.Editable = false;
	}else{
		tabMain.tab5.grdStrTest.Editable = true;
	}
	if (dsUseGbnItem.GetColumn(0,"F") == 0){			//¸éÁ¢
		tabMain.tab3.grdSndSeltItem3.Editable = false;
	}else{
		tabMain.tab3.grdSndSeltItem3.Editable = true;
	}
	if (dsUseGbnItem.GetColumn(0,"C") == 0){			//ÀÚÀü°Å
		tabMain.tab1.grdSndSeltItem.Editable = false;
	}else{
		for (var i=0;i<dsUseItem.RowCount();i++){  		// ÀÚÀü°Å ÃøÁ¤ °ü·Ã ·ÎÁ÷ 
			var itemCode = dsUseItem.GetColumn(i,"ITEM_CD");
			switch(itemCode){
			   case "301" :		//200m			
				tabMain.tab1.grdSndSeltItem.SetCellProp("Body",3,"Edit","mask");
				break;
			   case "302" :		//333m	
				tabMain.tab1.grdSndSeltItem.SetCellProp("Body",5,"Edit","mask");
				break;
			   case "303" :		//500m		
				tabMain.tab1.grdSndSeltItem.SetCellProp("Body",7,"Edit","mask");
				break;
			   case "304" :		//1000m
				tabMain.tab1.grdSndSeltItem.SetCellProp("Body",9,"Edit","mask");
				break;
			   case "305" :		//2000m
				tabMain.tab1.grdSndSeltItem.SetCellProp("Body",11,"Edit","mask");
				break;	
			}
		}
	}	
}
// Á¶È¸½Ã check
function fcSearchCheck(obj) {

    if( fnc_IsNull(cbRacerPerioNo.Value) ) {
        // ±â¼ö ´©¶ô
        fnc_Message(SNIS_CAN_0008, "N", "±â¼ö");
        return false;
    }
	return true;
}
//Á¶È¸¹öÆ° Å¬¸¯ ½Ã
function fcSearch(obj) {
 
   if ( !fcSearchCheck(obj) ) return;

	 fcSearchUseGbnItem();  //2008.05.26 update
}

/*-------------------------------------------------------------------------------------------------+
>>  ÀúÀå½Ã check
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
       
		if ( !fnc_DatasetChangeCheck(dsSndSeltExam)
				&& !fnc_DatasetChangeCheck(dsSndSeltExam1)
				&& !fnc_DatasetChangeCheck(dsSndSeltExam2)
				&& !fnc_DatasetChangeCheck(dsSndSeltExam3)
				&& !fnc_DatasetChangeCheck(dsSndSeltExam4)){
				fnc_Message(SNIS_COM_1006, "N");	
				return false;
			}
			return true;
} 

/*-------------------------------------------------------------------------------------------------+
>>  ÀúÀå ¹öÆ° Å¬¸¯ 
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";  

    if ( !fcSaveCheck() ) return;
    
	if(tabMain.TabIndex != 4){           						// ÀÚÀü°Å ½Ç±â  
	
		sServiceName     = "d02000008-service:saveSndSeltExam";
		sInDataSet       = "dsSndSeltExam=dsSndSeltExam:u dsSndSeltExam1=dsSndSeltExam1:u dsSndSeltExam2=dsSndSeltExam2:u dsSndSeltExam3=dsSndSeltExam3:u" ;
		sOutDataSet      = "dsSndSeltExam=dsSndSeltExam dsSndSeltExam1=dsSndSeltExam1 dsSndSeltExam2=dsSndSeltExam2 dsSndSeltExam3=dsSndSeltExam3" ;
		
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
	}else{  // Áý°èÇ¥¿¡¼­ ÀúÀå ¹öÆ°À» ´©¸£¸é ÃÖÁ¾ÇÕ°Ý¿©ºÎ¸¦ ¹¯´Â´Ù
		var confirmMsg = Confirm("ÃÖÁ¾ ÇÕ°ÝÀÚ¸¦ ÀúÀåÇÒ±î¿ä?");
		
		if ( confirmMsg ) {
			sServiceName     = "d02000008-service:saveSndyn";
			sInDataSet       = " dsSndSeltExam4=dsSndSeltExam4:u ";
			sOutDataSet      = " dsSndSeltExam4=dsSndSeltExam4 ";
			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
		}
	}
	
} // fcSave END


