+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
    switch(sServiceName) { 
         case "sServiceName == common-service:searchCode" : // °øÅëÄÚµå Á¶È¸½Ã
              break;	           
         case "d02000008-service:searchPerioNo"      :   //  ±â¼öcombo¿¡ setting Æ®·£Á§¼ÇÈÄ ÄÝ¹é Ã³¸®   
              break;
         case "d02000008-service:searchUseGbnItem"   :
			   fcStartEndDate();
			   fcUseEditable();
			   fcSearchSndSeltExam();
			  break;
		 case "d02000008-service:searchSndSeltExam" :
			   fcFstYN();
			  break;
		 case "d02000008-service:saveSndyn" :
				var confirmMsg = Confirm("ÇÕ°ÝÀÚ¸¦ ÈÄº¸»ý ½Å»ó¿¡ ÀúÀå ÇÒ±î¿ä?");			
				if ( confirmMsg ) {
					fcCandNo();			
				}
			break;
         default :
            break;
    
    }
    //¿À·ù°¡ ¾øÀÌ Á¤»ó Ã³¸®µÇ¾ú´Ù¸é...
	fcPostProcess(sServiceName);
}

