function dsSndSeltExam2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{	
   var sTotScr      = 0;	   // ÇÊ±â½ÃÇè ÇÕ°èÁ¡¼ö	
    var nScrRate     = 0;
	if(Trim(strColumnID) == "TOT_SCR") {	  
	   nChangeRow        = dsSndSeltExam2.rowposition;
	   sTotScr           = toNumber(dsSndSeltExam2.GetColumn(nChangeRow,"TOT_SCR"));
	   nScrRate 		 = toNumber(dsSndExamRateBas.GetColumn(0,"FST_ASSIGN_SCR_RATE")) * 0.01; 
	   dsSndSeltExam2.SetColumn(nChangeRow,"CHNG_SCR",Round((sTotScr * nScrRate),2)); 
	}    
}  
function dsSndSeltExam3_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{	
	var iRec = ToFloat(varNewValue);
	var iScr =  dsSndSeltExam3.GetColumn(nRow,"P1_SCR")
			   +dsSndSeltExam3.GetColumn(nRow,"P2_SCR")
			   +dsSndSeltExam3.GetColumn(nRow,"P3_SCR")
			   +dsSndSeltExam3.GetColumn(nRow,"P4_SCR")
			   +dsSndSeltExam3.GetColumn(nRow,"P5_SCR");
	
	switch(strColumnID){
		case "P1_SCR":
		case "P2_SCR":
		case "P3_SCR":
		case "P4_SCR":
		case "P5_SCR":
		dsSndSeltExam3.SetColumn(nRow,"TOT_SCR",iScr);
		dsSndSeltExam3.SetColumn(nRow,"CHNG_SCR",Round(iScr*(toNumber(dsSndExamRateBas.GetColumn(0, "FST_ASSIGN_SCR_RATE")) / 100),2));
		break;
		
	}

}  
