function tabMain_OnChanged(obj,nOldIndex,nNewindex)
{
	switch(nNewindex){
    case 0 :
			btnProcess.Enable = false;
			dsSndExamRateBas.Filter("ITEM_GBN_CD = '003'");
			break;
    case 1 :
			btnProcess.Enable = true;
			dsSndExamRateBas.Filter("ITEM_GBN_CD = '004'");
			break;
    case 2 :
			btnProcess.Enable = false;
			dsSndExamRateBas.Filter("ITEM_GBN_CD = '001'");
			break;
	case 3:	
			btnProcess.Enable = false;
			dsSndExamRateBas.Filter("ITEM_GBN_CD = '006'");
			break;
	default :
			btnProcess.Enable = false;
	 break;	
    }
}

