    }
    else if(tabMain.TabIndex == 3){          
      fnc_OpenExcelExport("tabMain.tab3.grdSndSeltExam3");
    }
    else if(tabMain.TabIndex == 4){          
      fnc_OpenExcelExport("tabMain.tab4.grdSndSeltExam4");
    }
}

