/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
    switch(sServiceName) { 
         case "sServiceName == common-service:searchCode" : // 공통코드 조회시
              break;	           
         case "d02000008-service:searchPerioNo"      :   //  기수combo에 setting 트랜젝션후 콜백 처리   
              break;
         case "d02000008-service:searchUseGbnItem"   :
			   fcStartEndDate();
			   fcUseEditable();
			   fcSearchSndSeltExam();
			  break;
		 case "d02000008-service:searchSndSeltExam" :
			   fcFstYN();
			  break;
		 case "d02000008-service:saveSndyn" :
				var confirmMsg = Confirm("합격자를 후보생 신상에 저장 할까요?");			
				if ( confirmMsg ) {
					fcCandNo();			
				}
			break;
         default :
            break;
    
    }
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

