>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}
/*-------------------------------------------------------------------------------------------------+
// 자전거 실기 종목 기록 입력시 환선점수를 계산하는 로직
+-------------------------------------------------------------------------------------------------*/
function dsSndSeltExam_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{		
	var iRec = ToFloat(varNewValue);
	var iScr = 0;
	
	switch(strColumnID){
		case "REC_200"  : dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='301'"); break;
		case "REC_333"  : dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='302'"); break;
		case "REC_500"  : dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='303'"); break;
		case "REC_1000" : dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='304'"); break;
		case "REC_2000" : dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='305'"); break;
	default : break;
	}
	
	for(i=0;i<dsSeltChng.RowCount();i++){
			var iStartRec = toNumber(dsSeltChng.GetColumn(i, "START_REC")); 
			var iEndRec = toNumber(dsSeltChng.GetColumn(i, "END_REC"));
			if( (iRec >= iStartRec)&&(iRec <=iEndRec)){
				 iScr = dsSeltChng.getColumn(i,"BAS_SCR");
			 }
		}	
	switch(strColumnID){
		case "REC_200"  : dsSndSeltExam.SetColumn(nRow,"SCR_200",iScr);   break;
		case "REC_333"  : dsSndSeltExam.SetColumn(nRow,"SCR_333",iScr);   break;
		case "REC_500"  : dsSndSeltExam.SetColumn(nRow,"SCR_500",iScr);   break;
		case "REC_1000" : dsSndSeltExam.SetColumn(nRow,"SCR_1000",iScr);  break;
		case "REC_2000" : dsSndSeltExam.SetColumn(nRow,"SCR_2000",iScr);  break;
	default : break;
	}
	fcAvgScr(obj, nRow);// 평균,환산점수
}	
/*-------------------------------------------------------------------------------------------------+
// 체력측정 종목 기록 입력시 환선점수를 계산하는 로직
+-------------------------------------------------------------------------------------------------*/
function dsSndSeltExam1_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var iRec = ToFloat(varNewValue);
	var iScr = 0;
	
	switch(strColumnID){
		case "GRIP_LEFT_REC"   : dsSeltChng.Filter("ITEM_GBN_CD = '004' AND ITEM_CD ='401'"); break;
		case "GRIP_RIGHT_REC"  : dsSeltChng.Filter("ITEM_GBN_CD = '004' AND ITEM_CD ='402'"); break;
	default : break;
	}
	
	for(i=0;i<dsSeltChng.RowCount();i++){
			var iStartRec = toNumber(dsSeltChng.GetColumn(i, "START_REC")); 
			var iEndRec = toNumber(dsSeltChng.GetColumn(i, "END_REC"));
			if( (iRec >= iStartRec)&&(iRec <=iEndRec)){
				 iScr = dsSeltChng.getColumn(i,"BAS_SCR");
				// alert(iScr);
			 }
		}	
	switch(strColumnID){
		case "GRIP_LEFT_REC"   : dsSndSeltExam1.SetColumn(nRow,"GRIP_LEFT_SCR",iScr);   break;
		case "GRIP_RIGHT_REC"  : dsSndSeltExam1.SetColumn(nRow,"GRIP_RIGHT_SCR",iScr);   break;
	default : break;
	}
	fcAvgScr(obj, nRow);// 평균,환산점수
}

/*-------------------------------------------------------------------------------------------------+
>>  평균점수 구하는 함수 
+-------------------------------------------------------------------------------------------------*/
function fcAvgScr(obj, nRow) {
	
	var scrCnt = 0;
	var totScr = 0;
	var avgScr = 0;    
	 if(obj.id == "dsSndSeltExam") {
        for(var i=0; i<dsSndSeltExam.rowcount; i++) {  
            if(i == nRow) {
                if(toNumber(dsSndSeltExam.GetColumn(i,"REC_200")) > 0) {      
                    scrCnt++;
                    totScr += ToInteger(dsSndSeltExam.GetColumn(i,"SCR_200"));
                }
                if(toNumber(dsSndSeltExam.GetColumn(i,"REC_333")) > 0) {
                    scrCnt++;
                    totScr += ToInteger(dsSndSeltExam.GetColumn(i,"SCR_333"));
                }
                if(toNumber(dsSndSeltExam.GetColumn(i,"REC_500")) > 0) {
                    scrCnt++;
                    totScr += ToInteger(dsSndSeltExam.GetColumn(i,"SCR_500"));
                }
                if(toNumber(dsSndSeltExam.GetColumn(i,"REC_1000")) > 0) {
                    scrCnt++;
                    totScr += ToInteger(dsSndSeltExam.GetColumn(i,"SCR_1000"));
                }				
                if(toNumber(dsSndSeltExam.GetColumn(i,"REC_2000")) > 0) {
                    scrCnt++;
                    totScr += ToInteger(dsSndSeltExam.GetColumn(i,"SCR_2000"));
                }
                avgScr = Round(ToNumber(totScr / scrCnt),2);
                dsSndSeltExam.SetColumn(i,"AVG_SCR",avgScr); //평균점수 셋팅
                dsSndSeltExam.SetColumn(i,"CHNG_SCR",Round(avgScr * (toNumber(dsSndExamRateBas.GetColumn(0, "FST_ASSIGN_SCR_RATE")) / 100),2));
                break;
            }
        }
        }else if(obj.id == "dsSndSeltExam1"){
           for(var i=0; i<dsSndSeltExam1.rowcount; i++) {  
            if(i == nRow) {
                if(toNumber(dsSndSeltExam1.GetColumn(i,"GRIP_LEFT_REC")) > 0) {
                    scrCnt++;
                    totScr += ToInteger(dsSndSeltExam1.GetColumn(i,"GRIP_LEFT_SCR"));
                }
                if(toNumber(dsSndSeltExam1.GetColumn(i,"GRIP_RIGHT_REC")) > 0) {
                    scrCnt++;
                    totScr += ToInteger(dsSndSeltExam1.GetColumn(i,"GRIP_RIGHT_SCR"));
                }
                if(toNumber(dsSndSeltExam1.GetColumn(i,"LEG_LEFT_REC")) > 0) {
                    scrCnt++;
                    totScr += ToInteger(dsSndSeltExam1.GetColumn(i,"LEG_LEFT_SCR"));
                }
                if(toNumber(dsSndSeltExam1.GetColumn(i,"LEG_RIGHT_REC")) > 0) {
                    scrCnt++;
                    totScr += ToInteger(dsSndSeltExam1.GetColumn(i,"LEG_RIGHT_SCR"));
                }
                if(toNumber(dsSndSeltExam1.GetColumn(i,"LEG_LEFT_REC2")) > 0) {
                    scrCnt++;
                    totScr += ToInteger(dsSndSeltExam1.GetColumn(i,"LEG_LEFT_SCR2"));
                }
                if(toNumber(dsSndSeltExam1.GetColumn(i,"LEG_RIGHT_REC2")) > 0) {
                    scrCnt++;
                    totScr += ToInteger(dsSndSeltExam1.GetColumn(i,"LEG_RIGHT_SCR2"));
                }
                if(toNumber(dsSndSeltExam1.GetColumn(i,"BELLY_REC")) > 0) {
                    scrCnt++;
                    totScr += ToInteger(dsSndSeltExam1.GetColumn(i,"BELLY_SCR"));
                }
                if(toNumber(dsSndSeltExam1.GetColumn(i,"MAX_PWR_REC")) > 0) {
                    scrCnt++;
                    totScr += ToInteger(dsSndSeltExam1.GetColumn(i,"MAX_PWR_SCR"));
                }
                if(toNumber(dsSndSeltExam1.GetColumn(i,"AVG_PWR_REC")) > 0) {
                    scrCnt++;
                    totScr += ToInteger(dsSndSeltExam1.GetColumn(i,"AVG_PWR_SCR"));
                }
                 if(toNumber(dsSndSeltExam1.GetColumn(i,"TIRE_IDX_REC")) > 0) {
                    scrCnt++;
                    totScr += ToInteger(dsSndSeltExam1.GetColumn(i,"TIRE_IDX_SCR"));
                }
                avgScr = Round(ToNumber(totScr / scrCnt),2);
                dsSndSeltExam1.SetColumn(i,"AVG_SCR",avgScr); //평균점수 셋팅
                dsSndSeltExam1.SetColumn(i,"CHNG_SCR",Round(avgScr * (toNumber(dsSndExamRateBas.GetColumn(0, "FST_ASSIGN_SCR_RATE")) / 100),2));
                break;
            }
        }//.for
    }    //if
}
