}    
/*-------------------------------------------------------------------------------------------------+
>>  기수콤보  설정
+-------------------------------------------------------------------------------------------------*/
function fcSetPerioNo()
{   
    var sServiceName = "d02000008-service:searchPerioNo";
    var sInDataSet   = "";
