function fccalculation(iPer){
	
	var iScr = 0;
	for(i=0;i<dsSeltChng.RowCount();i++){
			var iStartRec = toNumber(dsSeltChng.GetColumn(i, "START_REC")); 
			var iEndRec = toNumber(dsSeltChng.GetColumn(i, "END_REC"));
			if( (iPer >= iStartRec)&&(iPer <=iEndRec)){
				 iScr = dsSeltChng.getColumn(i,"BAS_SCR");
			 }
		}
	return iScr;
}
// 합격자 check
function fcFstYN(){

	var iCnt = dsSndSeltExam4.RowCount();
	var iCycleCnt = toNumber(dsUseGbnItem.GetColumn(0,"C"));  // 자전거 시험 적용되는 종목 개수
	var iHealth   = toNumber(dsUseGbnItem.GetColumn(0,"D"));    // 체력즉정  시험 적용 종목  개수 
	var iCycYnCnt = 0;
	var i_HealCnt = 0;
	var i_PassCnt = 0;
	var i_Pass = "";
	var i_racerCnt = toNumber(dsUseGbnItem.GetColumn(0,"CNT"));
	
	
	dsSndSeltExam1.Sort("TIRE_IDX_REC",0);
	
	for(var  i=0; i<iCnt; i++){
	
		iCycYnCnt = 0; // 데이타 -> 자전거 시험 적용되는 종목 개수
		i_HealCnt = 0; // 데이타 -> 체력즉정  시험 적용 종목  개수 
		i_Pass = "";
		// 자전거 (60점 이상인 자 check)
		if(!fnc_isNull(dsSndSeltExam4.GetColumn(i,"SCR_200"))){
			if((dsSndSeltExam4.GetColumn(i,"SCR_200")) >=60){
				iCycYnCnt++;
			}}
		if(!fnc_isNull(dsSndSeltExam4.GetColumn(i,"SCR_333"))){
			if((dsSndSeltExam4.GetColumn(i,"SCR_333")) >=60){
				iCycYnCnt++;
			}}
		if(!fnc_isNull(dsSndSeltExam4.GetColumn(i,"SCR_500"))){
			if((dsSndSeltExam4.GetColumn(i,"SCR_500")) >=60){
				iCycYnCnt++;
			}}
		if(!fnc_isNull(dsSndSeltExam4.GetColumn(i,"SCR_1000"))){
			if((dsSndSeltExam4.GetColumn(i,"SCR_1000")) >=60){
				iCycYnCnt++;
			}}
		if(!fnc_isNull(dsSndSeltExam4.GetColumn(i,"SCR_2000"))){
			if((dsSndSeltExam4.GetColumn(i,"SCR_2000")) >=60){
				iCycYnCnt++;
			}}
		if(iHealth > 0 ){   //체력측정 한다(1점 이상인 자 check)
			if(!fnc_isNull(dsSndSeltExam4.GetColumn(i,"GRIP_LEFT_SCR"))){
				if((dsSndSeltExam4.GetColumn(i,"GRIP_LEFT_SCR")) >=1){
				i_HealCnt++;
				}}
			if(!fnc_isNull(dsSndSeltExam4.GetColumn(i,"GRIP_RIGHT_SCR"))){
				if((dsSndSeltExam4.GetColumn(i,"GRIP_RIGHT_SCR")) >=1){
				i_HealCnt++;
				}}	
			if(!fnc_isNull(dsSndSeltExam4.GetColumn(i,"LEG_LEFT_SCR"))){
				if((dsSndSeltExam4.GetColumn(i,"LEG_LEFT_SCR")) >=1){
				i_HealCnt++;
				}}		
			if(!fnc_isNull(dsSndSeltExam4.GetColumn(i,"LEG_RIGHT_SCR"))){
				if((dsSndSeltExam4.GetColumn(i,"LEG_RIGHT_SCR")) >=1){
				i_HealCnt++;
				}}						
			if(!fnc_isNull(dsSndSeltExam4.GetColumn(i,"LEG_LEFT_SCR2"))){
				if((dsSndSeltExam4.GetColumn(i,"LEG_LEFT_SCR2")) >=1){
				i_HealCnt++;
				}}						
			if(!fnc_isNull(dsSndSeltExam4.GetColumn(i,"LEG_RIGHT_SCR2"))){
				if((dsSndSeltExam4.GetColumn(i,"LEG_RIGHT_SCR2")) >=1){
				i_HealCnt++;
				}}		
			if(!fnc_isNull(dsSndSeltExam4.GetColumn(i,"BELLY_SCR"))){
				if((dsSndSeltExam4.GetColumn(i,"BELLY_SCR")) >=1){
				i_HealCnt++;
				}}						
			if(!fnc_isNull(dsSndSeltExam4.GetColumn(i,"MAX_PWR_SCR"))){
				if((dsSndSeltExam4.GetColumn(i,"MAX_PWR_SCR")) >=1){
				i_HealCnt++;
				}}
			if(!fnc_isNull(dsSndSeltExam4.GetColumn(i,"AVG_PWR_SCR"))){
				if((dsSndSeltExam4.GetColumn(i,"AVG_PWR_SCR")) >=1){
				i_HealCnt++;
				}}						
			if(!fnc_isNull(dsSndSeltExam4.GetColumn(i,"TIRE_IDX_SCR"))){
				if((dsSndSeltExam4.GetColumn(i,"TIRE_IDX_SCR")) >=1){
				i_HealCnt++;
				}}	
		}//체력측정
		if(iHealth > 0 ){  //체력측정 시험볼 경우
		
			if(	(iCycleCnt == iCycYnCnt)&&(iHealth == i_HealCnt) ){
					i_Pass = "Y";
					i_PassCnt++;
			}else{
					i_Pass = "N";
			}
		}else{			   // 자전거 시험
			if(iCycleCnt == iCycYnCnt){
					i_Pass = "Y";
					i_PassCnt++;
			}else{
					i_Pass = "N";
			}
		}	
		if(i_PassCnt > i_racerCnt){
		
			i_Pass = "N";
		}
		dsSndSeltExam4.SetColumn(i,"SUCCESS_YN",i_Pass);
	} //for	
	i_racerCnt = toNumber(dsUseGbnItem.GetColumn(0,"CNT"));
}
