/*-------------------------------------------------------------------------------------------------+
>>  Clear 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
 
  if ( obj.id == "btnCancel" || obj.id == "btnSearch") {  
        //dsCandIdentList.ClearData(); 
        dsCandIdent.ClearData();
        dsSchlCarr.ClearData();
        dsInvwCntnt.ClearData();
        dsRacerReln.ClearData();
        dsCarr.ClearData();
        dsFmly.ClearData();
        dsSpecDesc.ClearData();
        dsPrizPenalScr.ClearData();
    }    
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearchCheck(obj) {
    
	fcInit(obj);
	
	if(fnc_IsNull(cbRacerPerioNo.Value)) {
        
        //조회할 자료가 없음
		fnc_Message(SNIS_CAN_0008, "N","기수");
		return false;
	}
	
	return true;
}
//조회버튼 클릭 시
function fcSearch(obj) {
   
    if ( !fcSearchCheck(obj) ) return;
    
    var sServiceName = "d02000009-service:searchCandIdentList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsCandIdentList=dsCandIdentList";
    
    // 기수, 성명으로 후보생 신상 테이블을 조회하여 그리드에 출력한다.	
	fcd_AddParam("racerPerioNo", Trim(cbRacerPerioNo.Value));
	fcd_AddParam("nmKor", Trim(edNm.text));
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false, "S");
}


