function fcSaveProc(nCandNo){	//실제 저장
	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	
	if(Length(nCandNo)>0){//키가 넘어 왔을 경우에만 키를 셋팅. 아닐 경우는 그냥 처리.
		dsCandIdent.SetColumn(0,"CAND_NO",nCandNo);
	}
	
	sServiceName     = "d02000009-service:saveCandIdent";    
	
	sInDataSet       = "dsCandIdent=dsCandIdent:a";
	sInDataSet		+=" dsFmly=dsFmly:u";//가족관계
	sInDataSet		+=" dsSchlCarr=dsSchlCarr:u";//학력
	sInDataSet		+=" dsCarr=dsCarr:u";//경력
	sInDataSet		+=" dsRacerReln=dsRacerReln:u";//관계
	sInDataSet		+=" dsInvwCntnt=dsInvwCntnt:u";//후보생 면담사항 조회
	sInDataSet		+=" dsSpecDesc=dsSpecDesc:u";//후보생 특이사항 
	sInDataSet		+=" dsPrizPenalScr=dsPrizPenalScr:u";//후보생 상벌사항
	sInDataSet		+=" dsSpecDesc=dsSpecDesc:u";//후보생 특이사항
	sInDataSet		+=" dsUploadFile=dsUploadFile:u";//첨부파일
	
	sOutDataSet      = "";
	
	if(dsUploadFile.GetRowCount()>0){
		saveFile();//파일저장
		dsCandIdent.SetColumn(0,"PTO_URL",dsUploadFile.GetColumn(0,"FILE_URL"));
	}
	
	trace(dsCandIdent.SaveXML());
	trace(dsSpecDesc.SaveXML());
	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false,"U");
}

function saveFile(obj){
	if( dsUploadFile.GetRowCount() > 0 ) {
		if(dsUploadFile.getColumn(0, "CHK") == "1" ) {
			var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
										, dsUploadFile.GetColumn(0, "FILE_URL" )
										, dsUploadFile.GetColumn(0, "FILE_NAME" )
										, "RACER");
			
			var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFile.GetColumn(0, "FILE_URL" ));

			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일 업로드 에러발생");
				return false;
			}
			//dsUploadFile.SetColumn(i, "SEQ_NO"      , dsOutNextSeqNo.getColumn(0,"NEXTSEQNO") );
			//dsUploadFile.SetColumn(i, "FILE_SEQ"    , toInteger(dsOutNextSeqNo.getColumn(0,"NEXTFILESEQNO")) + toInteger(i) );
			
			dsUploadFile.SetColumn(0, "FILE_URL"    , rtn_arr[1] );//실제 파일 경로.
			dsUploadFile.SetColumn(0, "FILE_SIZE"   , fileSize );
		} // end if
	} // end for   
}


