>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(){
		
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "d02009009.jrf";          //레포트 파일명
	var sDataSet  = "dsCandIdent=dsCandIdent dsFmly=dsFmly dsSchlCarr=dsSchlCarr dsCarr=dsCarr dsRacerReln=dsRacerReln dsInvwCntnt=dsInvwCntnt dsPrizPenalScr=dsPrizPenalScr dsSpecDesc=dsSpecDesc";    //"UBI 쿼리명, MIP Dataset명"
		
	//선수사진경로 가져오기
    var sPtoName = dsCandIdent.GetColumn(0, "PTO_URL");
    
    //if ( fnc_IsNull(sPtoName) ) return;
    //var sPhysicalFileName = split(sPtoName, "\\");
    var sPhysicalFileName = split(sPtoName, "//");
    var ptourl = GBL_IMAGELINK_URL + sPhysicalFileName[length(sPhysicalFileName) - 1];
	//alert(ptourl);
	var sArg  = " IMG_URL="+ptourl; 
	
	
	//var sArg      = "STND_YEAR="+CURR_DT; 
	//	sArg += " CAND_NO="+ replace(dsBlngCertList.getColumn(i, "CAND_NO"), ' ', '@'); 
	
	var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
	
	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}


