/*-------------------------------------------------------------------------------------------------+
>>  Help Popup Open
+-------------------------------------------------------------------------------------------------*/
function fcHelpDialoge(obj,nX,nY) {
	var sSearchKind = "";
	var sInputValue = "";
    var sResponse; 
	
	switch(obj.id){
		case "imgZip1":
			sResponse = fcd_HelpDialoge("POST","","");
			if(Length(sResponse)){
				dsCandIdent.SetColumn(0,"POST_NO",substr(sResponse[0],0,3)+substr(sResponse[0],4,3));
				dsCandIdent.SetColumn(0,"ADDR1",sResponse[1]);
			}
			break;
		case "imgZip2":
			sResponse = fcd_HelpDialoge("POST","","");
			if(Length(sResponse)){
				dsCandIdent.SetColumn(0,"PERMNT_POST_NO",substr(sResponse[0],0,3)+substr(sResponse[0],4,3));
				dsCandIdent.SetColumn(0,"PERMNT1",sResponse[1]);
			}
			break;
	}
}

