/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
    switch(sServiceName) {
        // 공통코드 조회시
        case "common-service:searchCode":
			cbRacerPerioNo.Index = 0;
			fcSearch();
			break;
			
        //  후보생 리스트  setting 트랜젝션후 콜백 처리 
        case "d02000009-service:searchCandIdentList":
			meResNo.Enable = false;
			searchCandIdent(0);
			break;
              
		case "d02000009-service:searchCandIdent":
			//이미지Set
			var img = split(dsCandIdent.GetColumn(0,"PTO_URL"),"/");
			imgPhoto.ImageID = GBL_IMAGELINK_URL+img[Length(img)-1];
			Trace(" imgPhoto.ImageID="+ imgPhoto.ImageID);

			meResNo.Enable = false;
			if(dsSpecDesc.GetRowCount()==0){
				dsSpecDesc.AddRow();
			}
			
			if ( toString(dsCandIdent.GetColumn(0,"WGHT")).length <= 3) {
				dsCandIdent.SetColumn(0, "WGHT", "0" +dsCandIdent.GetColumn(0,"WGHT")); 
			}
			if ( toString(dsCandIdent.GetColumn(0,"SIT_HGHT")).length <= 3) {
				dsCandIdent.SetColumn(0, "SIT_HGHT", "0" +dsCandIdent.GetColumn(0,"SIT_HGHT")); 
			}
			if ( toString(dsCandIdent.GetColumn(0,"BREAST_SIZE")).length <= 3) {
				dsCandIdent.SetColumn(0, "BREAST_SIZE", "0" +dsCandIdent.GetColumn(0,"BREAST_SIZE")); 
			}
			if ( toString(dsCandIdent.GetColumn(0,"BELLY_CIR")).length <= 3) {
				dsCandIdent.SetColumn(0, "BELLY_CIR", "0" +dsCandIdent.GetColumn(0,"BELLY_CIR")); 
			}
			if ( toString(dsCandIdent.GetColumn(0,"HEAP_SIZE")).length <= 3) {
				dsCandIdent.SetColumn(0, "HEAP_SIZE", "0" +dsCandIdent.GetColumn(0,"HEAP_SIZE")); 
			}
			if ( toString(dsCandIdent.GetColumn(0,"FEM_RIGHT")).length <= 3) {
				dsCandIdent.SetColumn(0, "FEM_RIGHT", "0" +dsCandIdent.GetColumn(0,"FEM_RIGHT")); 
			}
			if ( toString(dsCandIdent.GetColumn(0,"FEM_LEFT")).length <= 3) {
				dsCandIdent.SetColumn(0, "FEM_LEFT", "0" +dsCandIdent.GetColumn(0,"FEM_LEFT")); 
			}
			
			break;
		case "d02000009-service:keyCandIdent":
			fcSaveProc(dsCandSeq.GetColumn(0,"SEQ"));
 
        case "d02000009-service:saveCandIdent":
            fcSearch(); //저장 시 리스트 새로고침
			break;
		case "d02000009-service:deleteCandIdent" :
			dsCandIdent.ClearData();
			dsFmly.ClearData();
			dsSchlCarr.ClearData();
			dsCarr.ClearData();
			dsRacerReln.ClearData();
			dsInvwCntnt.ClearData();
			dsPrizPenalScr.ClearData();
			imgPhoto.ImageID = "";
			
			fcSearch();
			break;
	}
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

  	dsUploadFile.ClearData();
	//DataSet의 Header 정보가 있을 때  Add 처리
	
	switch(obj.GetForm().id) {
	
	     case "divBtnRow"   :
	       
			dsCandIdent.ClearData();
			dsSchlCarr.ClearData();
			dsInvwCntnt.ClearData();
			dsRacerReln.ClearData();
			dsCarr.ClearData();
			dsFmly.ClearData();  
			dsSpecDesc.ClearData();
			dsPrizPenalScr.ClearData();
			
			if ( fnc_DatasetChangeCheck(dsCandIdent) ) {
				fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
				//  meCandNo.SetFocus();
				return;
			}
           
			dsCandIdent.AddRow();
			dsSpecDesc.AddRow();
			meResNo.Enable = true;

	      break;

	    case "divBtnRowSub1"  :
			if(fcCheckHead()){
				dsFmly.AddRow();
			}
			break;
		case "divBtnRowSub2"  :
			if(fcCheckHead()){
				dsSchlCarr.AddRow(); 
			}
			break;
		case "divBtnRowSub3"  :
			if(fcCheckHead()){
				dsCarr.AddRow(); 
			}
			break;
		case "divBtnRowSub4"  :
			if(fcCheckHead()){
				dsRacerReln.AddRow();
			}
			break;
		case "divBtnRowSub5"  :
			if(fcCheckHead()){
				dsInvwCntnt.AddRow();
			}
			break;
		case "divBtnRowSub6"  :
			if(fcCheckHead()){
				dsPrizPenalScr.AddRow();
			}
			break;
	    default : break;
            
	}

}
function fcCheckHead(){
	if(dsCandIdent.GetRowCount()==0){
		fnc_Message("후보생정보가 선택되어 있을경우에만 사용이 가능합니다.","N");
		return false;
	}
	return true;
}
/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
 	
 	if(fnc_IsNull(dsCandIdent.GetColumn(0,"RACER_PERIO_NO"))){
		fnc_Message("삭제할 내용이 없습니다.","N");
		return false;
	}
	if(dsCandIdent.GetRowCount()>0){//하단 리스트에서 클릭했을 경우에만 처리
		switch(obj.GetForm().id) {
		case "divBtnRow":
			if(dsCandIdent.GetRowCount() == 0){
				fnc_Message(SNIS_COM_1003,"N");
			}
			else if(confirm("선택된 정보를 삭제하시겠습니까?")){
				dsCandIdent.SetColumn(0,"DEL_YN","Y");

				sServiceName     = "d02000009-service:deleteCandIdent";
				sInDataSet       = " dsCandIdent=dsCandIdent:a ";
				sOutDataSet      = "";
				
				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
				//fnc_DatasetPrint(dsCandIdent);
			}
			
			break; 
		case "divBtnRowSub1"  :
			fnc_DeleteCheck(dsFmly, "CHK", "1");
			break;
		case "divBtnRowSub2"  :
			fnc_DeleteCheck(dsSchlCarr, "CHK", "1");
			break;
		case "divBtnRowSub3"  :
			fnc_DeleteCheck(dsCarr, "CHK", "1");
			break;
		case "divBtnRowSub4"  :
			fnc_DeleteCheck(dsRacerReln, "CHK", "1");
			break;
		case "divBtnRowSub5"  :
			fnc_DeleteCheck(dsInvwCntnt, "CHK", "1");
			break;
		case "divBtnRowSub6"  :
			fnc_DeleteCheck(dsPrizPenalScr, "CHK", "1");
			break;
		default : break;
		}
	}
	      

	
}


/*-------------------------------------------------------------------------------------------------+
>>  후보생 신상 상세 조회
+-------------------------------------------------------------------------------------------------*/

function grdCandIdentList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	searchCandIdent(nRow);
}

function searchCandIdent(nRow)
{
	dsUploadFile.ClearData();
    
    var sRacerPerioNo   =  dsCandIdentList.GetColumn(nRow,"RACER_PERIO_NO");
    var sCandNo         =  dsCandIdentList.getColumn(nRow,"CAND_NO");

    var sServiceName    = "d02000009-service:searchCandIdent";
    var sInDataSet      = "";
    var sOutDataSet     = "dsCandIdent=dsCandIdent";
		sOutDataSet		+=" dsFmly=dsFmly";//가족관계
		sOutDataSet		+=" dsSchlCarr=dsSchlCarr";//학력
		sOutDataSet		+=" dsCarr=dsCarr";//경력
		sOutDataSet		+=" dsRacerReln=dsRacerReln";//관계
		sOutDataSet		+=" dsInvwCntnt=dsInvwCntnt";//   후보생 면담사항 조회
		sOutDataSet		+=" dsSpecDesc=dsSpecDesc";//후보생 특이사항 
		sOutDataSet		+=" dsPrizPenalScr=dsPrizPenalScr";//   후보생 상벌사항
   
   
    
    if ( sRacerPerioNo == "" || sCandNo == "" ) return;
    fcd_AddParam("racerPerioNo", sRacerPerioNo);  //매개변수 : 기수
    fcd_AddParam("candNo", sCandNo);              //매개변수 : 후보생번호
       
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
} 

//   후보생 상벌점  조회
function searchPrizPenalScore(nChangeRow,sPrizPenalGbn,sItemCd,sPrizPenalCd)
{
   
  var sServiceName = "d02000009-service:searchPrizPenalScore";
  var sInDataSet   = "";
  var sOutDataSet  = "dsPrizPenalScore=dsPrizPenalScore";
    
  //상벌점  조회	  
  
  fcd_AddParam("itemCd", sItemCd);
  fcd_AddParam("prizPenalCd", sPrizPenalCd);
  fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);

} 


// 가족사항 유효성 체크 
function searchFmlyChk()
{
//alert("가족사항 유효성 체크");
 
    //if(tabMain.TabIndex == 0){
       if ( !fnc_DatasetChangeCheck(dsFmly))     
	   {
	     dsFmlyUpdateChk  = "N";
		//저장 할 자료가 없음
		//fnc_Message(SNIS_COM_1006, "N");	
		return false;
	   }
	   else  dsFmlyUpdateChk  = "Y";
    
     for(var i=0; i<dsFmly.rowcount; i++) {
       if( dsFmly.GetColumn(i,"RELN") == NULL) {
         fnc_Message(SNIS_COM_1034, "N", "관계");
         return false;
       }
       if( dsFmly.GetColumn(i,"NM") == NULL) {
         fnc_Message(SNIS_COM_1034, "N", "성명");
         return false;
       }
       if( dsFmly.GetColumn(i,"RES_NO") == NULL) {
         fnc_Message(SNIS_COM_1034, "N", "주민번호");
         return false;
       }
       var sResNo  = "";
       sResNo      = dsFmly.GetColumn(i,"RES_NO");
     
       if( sResNo.length < 13) {
         fnc_Message(SNIS_COM_1034, "N", "주민번호");
         return false;
       }
       if( dsFmly.GetColumn(i,"SCHL") == NULL) {
         fnc_Message(SNIS_COM_1034, "N", "학력");
         return false;
       }
       if( dsFmly.GetColumn(i,"RES_AREA") == NULL) {
         fnc_Message(SNIS_COM_1034, "N", "거주지역");
         return false;
       }       
     }  // for end     
  // }   //  tab0 if end
}
 

