// 특이사항 행추가
function addRowSpecDesc(){
	if(tabMain.TabIndex == 5){ 
	
		if ( fnc_DatasetChangeCheck(dsSpecDesc) ) {
			//  fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
			return;             
		}
		if( dsSpecDesc.rowcount <= 0) {
			if (cbRacerPerioNo.text == "") {
				fnc_Message(SNIS_CAN_D002, "N","기수"); 
				return;
			} 
			if ( dsCandIdent.GetColumn(0,"CAND_NO") == NULL) {
				fnc_Message(SNIS_CAN_D002, "N","후보생번호"); 
				return;  
			}
			dsSpecDesc.AddRow();
		}
	}
}

// 생년월일 셋팅
function setBirthDt()
{
	var sBirthDt   = "";
	sBirthDt       = substr(meResNo.value,0,6);
	sBirthDt       = "19" + sBirthDt; 
	calBirthDt.value = sBirthDt;
}


function dsPrizPenalScr_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex){	
	if(strColumnID == "DUM_CD"){
		//보이기용 컬럼 변경시 실제컬럼을 변경처리.
		obj.SetColumn(nRow,"PRIZ_PENAL_CD",substr(varNewValue,3,3));
	}

	if(strColumnID == "PRIZ_PENAL_CD" || strColumnID == "PRIZ_PENAL_GBN"){
		if(fnc_IsNull(obj.GetColumn(nRow,"PRIZ_PENAL_CD"))){
			return;
		}

		var sReturn = Array(2);
		var nFindRow = dsPrizPenalCd.FindRow("PRIZ_PENAL_CD",obj.GetColumn(nRow,"DUM_CD"));

		sReturn[0] = dsPrizPenalCd.GetColumn(nFindRow,"ADD_SCR");
		sReturn[1] = dsPrizPenalCd.GetColumn(nFindRow,"PENAL_SCR");
		
		//상점일경우와 벌점인경우 점수셋팅
		if(dsPrizPenalScr.GetColumn(nRow,"PRIZ_PENAL_GBN")=="001"){	//상이면
			obj.SetColumn(nRow,"PRIZ_SCR",sReturn[0]);
			obj.SetColumn(nRow,"PENAL_SCR","0");
		}
		else{//벌이면 
			obj.SetColumn(nRow,"PRIZ_SCR","0");
			obj.SetColumn(nRow,"PENAL_SCR",sReturn[1]);
		}
	} 
}




function fcFileSelect(obj){
	var sResponse = fcd_GetSelectFile();

	if(Length(sResponse)>0){
		dsUploadFile.ClearData();
		var nRow = dsUploadFile.AddRow();

		dsUploadFile.SetColumn(nRow,"CHK","1");
		dsUploadFile.SetColumn(nRow,"FILE_URL"	,sResponse[0] + "\\" + sResponse[1]);
		dsUploadFile.SetColumn(nRow,"FILE_NAME"	,sResponse[1]);

		dsCandIdent.SetColumn(0,"PTO_FILE",sResponse[1]);

		imgPhoto.ImageID = dsUploadFile.GetColumn(nRow,"FILE_URL");
	}
}

