function fcSearch() {
	var sServiceName = "d02000010-service:searchItemCode";
    var sInDataSet   = "";
    var sOutDataSet  = " dsItemCodeListValue=dsItemCodeListValue ";
        sOutDataSet += " dsPrizCodeListValue=dsPrizCodeListValue ";    
	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcItemCodeExistsCheck(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
    if(strColumnID == "ITEM_CD") {
        var sServiceName = "d02000010-service:searchItemCodeCheck";
        var sInDataSet   = "";
        var sOutDataSet  = " dsItemCodeCheckValue=dsItemCodeCheckValue ";
        
        fcd_AddParam("ITEM_CD", dsItemCodeListValue.getColumn(nRow, "ITEM_CD"));
        
        fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
    }
}

function fcPrizCodeExistsCheck(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
    if(strColumnID == "PRIZ_PENAL_CD") {
        var sServiceName = "d02000010-service:searchPrizCodeCheck";
        var sInDataSet   = "";
        var sOutDataSet  = " dsPrizCodeCheckValue=dsPrizCodeCheckValue ";
        
        fcd_AddParam("ITEM_CD", dsPrizCodeListValue.getColumn(nRow, "ITEM_CD"));
        fcd_AddParam("PRIZ_PENAL_CD", dsPrizCodeListValue.getColumn(nRow, "PRIZ_PENAL_CD"));
        
        fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
    }
}

function fcSaveCheck(obj) {  
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsItemCodeListValue) 
	  && !fnc_DatasetChangeCheck(dsPrizCodeListValue) ) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	
	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
    
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
    
    sServiceName = "d02000010-service:saveItemCode";
    sInDataSet   = " dsItemCodeListValue=dsItemCodeListValue:u ";
    sInDataSet   += " dsPrizCodeListValue=dsPrizCodeListValue:u ";
    
    sOutDataSet  = " dsItemCodeListValue=dsItemCodeListValue ";
    sOutDataSet  += " dsPrizCodeListValue=dsPrizCodeListValue ";
    
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


