***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
	// 조회 완료 후
	if ( sServiceName == "d02000010-service:searchItemCode" ) {    
        fcGetPrizCode(grdItemCD.clickedRow); 
	}
	
	if ( sServiceName == "d02000010-service:searchPrizCode" ) {
        // chk 컬럼 추가
        dsPrizCodeListValue.AddColumn("CHK");	
	}
	
	// 저장 완료 후
	if ( sServiceName == "d02000010-service:saveItemCode" ) {
		dsItemCodeListValue.currow;
        fcSearch();
	}	
	
	// 그룹코드 체크 후
	if ( sServiceName == "d02000010-service:searchItemCodeCheck") {
        // 동일한 그룹코드가 존재시
        if(dsItemCodeCheckValue.getColumn(0, "ITEM_CD") == 
            dsItemCodeListValue.getColumn(dsItemCodeListValue.currow, "ITEM_CD")) {
            fnc_Message(L_COM_ALT_0016, "N", "상벌점 조항 코드가");
            dsItemCodeListValue.setColumn(dsItemCodeListValue.currow, "ITEM_CD", "");
            grdItemCd.setFocus();
        }
    }
    
	// 상세코드 체크 후
	if ( sServiceName == "d02000010-service:searchPrizCodeCheck") {
        // 동일한 그룹코드가 존재시
        if(dsPrizCodeCheckValue.getColumn(0, "PRIZ_PENAL_CD") == 
            dsPrizCodeListValue.getColumn(dsPrizCodeListValue.currow, "PRIZ_PENAL_CD")) {
            fnc_Message(L_COM_ALT_0016, "N", "상벌점 항목 코드가");
            dsPrizCodeListValue.setColumn(dsPrizCodeListValue.currow, "PRIZ_PENAL_CD", "");
            return;
        }
    }   
}

