/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	switch ( obj.GetForm().id ) {
		case "itemBtnRow" :			
            if ( fnc_DatasetChangeCheck(dsItemCodeListValue) ) {
                fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
                return;
            }
            		
            dsItemCodeListValue.addRow();
			break;
		case "prizBtnRow" :
			var nRow = dsPrizCodeListValue.addRow();			
			dsPrizCodeListValue.FireEvent = false;
			dsPrizCodeListValue.SetColumn(nRow , "ITEM_CD" , 
				dsItemCodeListValue.GetColumn(dsItemCodeListValue.Currow,"ITEM_CD"));
			dsPrizCodeListValue.FireEvent = true;
			
            break;				
    } // end switch.case
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	switch ( obj.GetForm().id ) {
		case "itemBtnRow" :
            var sArg = "";

			if ( !fnc_DeleteCheck(dsItemCodeListValue, "chk", "1") ) return;

			dsItemCodeListValue_OnRowPosChanged();
			break;
		case "prizBtnRow" :
			if ( !fnc_DeleteCheck(dsPrizCodeListValue, "chk", "1") ) return;
            
			dsPrizCodeListValue_OnRowPosChanged();            
            break;
            
    } // end switch.case
}


/*-------------------------------------------------------------------------------------------------+
>>  Column의 값이 변경된 경우
+-------------------------------------------------------------------------------------------------*/
function dsItemCodeListValue_OnRowPosChanged(obj,nOldRow,nRow)
{
    var sArg = "";
    if ( dsItemCodeListValue.GetRowCount() == 0 ) return;
    
    if ( dsItemCodeListValue.GetRowCount() <= dsItemCodeListValue.currow ) {
        dsItemCodeListValue.FireEvent = false;
        dsItemCodeListValue.row = dsItemCodeListValue.GetRowCount() - 1;
        dsItemCodeListValue.SelectRow(dsItemCodeListValue.GetRowCount() - 1);
        dsItemCodeListValue.FireEvent = true;
    }

	if ( fnc_DatasetChangeCheck(dsItemCodeListValue) ) {
		if ( fnc_Message(SNIS_COM_1002, "Y") ) {
            dsItemCodeListValue.FireEvent = false;
            dsItemCodeListValue.row = nOldRow;
			fcSave();
            dsItemCodeListValue.FireEvent = true;
			return;
		}
	}
	
    //fcSearch();
}


function dsPrizCodeListValue_OnRowPosChanged(obj,nOldRow,nRow){
	var sArg = "";
    //if ( dsPrizCodeListValue.GetRowCount() == 0 ) return;
    if ( dsPrizCodeListValue.GetRowCount() <= dsPrizCodeListValue.currow ) {
        dsPrizCodeListValue.FireEvent = false;
        dsPrizCodeListValue.row = dsPrizCodeListValue.GetRowCount() - 1;
        dsPrizCodeListValue.SelectRow(dsPrizCodeListValue.GetRowCount() - 1);
        dsPrizCodeListValue.FireEvent = true;
    }

	if ( fnc_DatasetChangeCheck(dsPrizCodeListValue) ) {
		if ( fnc_Message(SNIS_COM_1002, "Y") ) {
            dsPrizCodeListValue.FireEvent = false;
            dsPrizCodeListValue.row = nOldRow;
			fcSave();
            dsPrizCodeListValue.FireEvent = true;
			return;
		}
	}
	
	//fcSearch();
}

