	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);    
	
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    if(!fnc_IsMandatory(cbPerioNo, "기수")){
		return false;
    }	
	return true;
}

function fcSearch() {
	if(!fcSearchCheck()){
		return;
	}
	
	var sServiceName = "d02000011-service:searchBlngCert";
    var sInDataSet   = "";
    var sOutDataSet  = " dsBlngCertList=dsBlngCertList dsBlingDate=dsBlingDate ";
    var pRacerName	 = "";
    
    fcd_AddParam("pRacerPerioNo", Trim(cbPerioNo.Value));
    fcd_AddParam("pRacerName", Trim(edRacerNm.Text));
		
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {  
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsBlngCertList)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	// 필수항목체크
	oDataSet          = dsBlngCertList;
	sMandatoryColID   = "ISSUE_DT|CAND_NO";
	sMandatoryColName = "발급일자|학번";
	oGrid             = grdBlngCert;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
    
	fnc_DataSetPrint(dsBlngCertList);
	
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
    
    sServiceName = "d02000011-service:saveBlngCert";
    sInDataSet   = " dsBlngCertList=dsBlngCertList:u ";
    
    //sOutDataSet  = " dsBlngCertList=dsBlngCertList ";
    
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", "U");
}
/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(){
	
	var strdate = dsBlingDate.GetColumn(0,"EDU_STR_DT");
	var enddate = dsBlingDate.GetColumn(0,"EDU_END_DT");
	strdate = substr(strdate,0,4)+"년"+substr(strdate,4,2)+"월"+substr(strdate,6,2)+"일";
	enddate = substr(enddate,0,4)+"년"+substr(enddate,4,2)+"월"+substr(enddate,6,2)+"일";
	
	for(var i = 0 ; i <= dsBlngCertList.GetRowCount(); i++) {
		if(dsBlngCertList.GetColumn(i, "CHK")=="1"){
			if(dsBlngCertList.GetColumn(i, "ISSUE_NM")!=null){
				var logImgUrl = GBL_REPORT_URL+"log.jpg";
				var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
				var sRptNm    = "d02009011.jrf";          //레포트 파일명
				var sDataSet  = "dsBlngCertList=dsBlngCertList";    //"UBI 쿼리명, MIP Dataset명"
				var sArg      = "ISSUE_NM="+replace(dsBlngCertList.getColumn(i, "ISSUE_NM")+' 호', ' ', '@');
					sArg += " CAND_NO="+ replace(dsBlngCertList.getColumn(i, "CAND_NO"), ' ', '@'); 	
					sArg += " NM_KOR="+ replace(dsBlngCertList.getColumn(i, "NM_KOR"), ' ', '@'); 	
					sArg += " RES_NO="+ replace(dsBlngCertList.getColumn(i, "RES_NO"), ' ', '@'); 	
					sArg += " ADDR="+ replace(dsBlngCertList.getColumn(i, "ADDR"), ' ', '@'); 	
					sArg += " USE_PRPS="+ replace(dsBlngCertList.getColumn(i, "USE_PRPS"), ' ', '@'); 	
					sArg += " SOSOK="+replace("경 륜 훈 련 원 ", ' ', '@'); 
					sArg += " STND_YEAR="+ CURR_DT; 
					sArg += " STR_DT="+ strdate;
					sArg += " END_DT="+ enddate;

				var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
				
				fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
			}
		}
	}	
}	
