/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
		
	if( sServiceName == "d02000011-service:searchBlngCert" ) {

	}
		
	//행추가 버튼 클릭 후
	if ( sServiceName == "d02000011-service:searchIssueName" ) {
        dsBlngCertList.setColumn(dsBlngCertList.currow, "ISSUE_DT", CURR_DT);  
        dsBlngCertList.setColumn(dsBlngCertList.currow, "RACER_PERIO_NO", cbPerioNo.Value);  
		fcGetRacer();
        grdBlngCert.SetFocus();
	}else if( sServiceName == "d02000011-service:searchBlngCert" ) {
	   	fnc_SetStatus(SNIS_CAN_0003,  dsBlngCertList.GetRowCount());
	}
	
	// 저장 완료 후
	if ( sServiceName == "d02000011-service:saveBlngCert" ) {
		fcSearch();
		grdBlngCert.SetFocus();
	}	
}

